"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[1477],{10:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"overlay-kit","metadata":{"permalink":"/blog/overlay-kit","source":"@site/blog/2025-02-14-state-management-without-state-management.md","title":"\ud504\ub860\ud2b8\uc5d4\ub4dc \uc0c1\ud0dc\uad00\ub9ac \uc798 \ud558\ub294 \ubc29\ubc95 - \\"\uc0c1\ud0dc\uad00\ub9ac\ub97c \ud558\uc9c0 \uc54a\ub294\ub2e4\\" (feat. overlay-kit)","description":"\ud504\ub85c\uadf8\ub798\ubc0d\uc5d0\uc11c\uc758 \ub9ce\uc740 \ubb38\uc81c\ub4e4\uc774 \uadf8\ub7ec\ud558\ub4ef\uc774 \uc0c1\ud0dc\uad00\ub9ac\ub294 \uc815\ub2f5\uc774 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.","date":"2025-02-14T00:00:00.000Z","formattedDate":"2025\ub144 2\uc6d4 14\uc77c","tags":[{"label":"frontend","permalink":"/blog/tags/frontend"},{"label":"state management","permalink":"/blog/tags/state-management"},{"label":"overlay-kit","permalink":"/blog/tags/overlay-kit"}],"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"overlay-kit","title":"\ud504\ub860\ud2b8\uc5d4\ub4dc \uc0c1\ud0dc\uad00\ub9ac \uc798 \ud558\ub294 \ubc29\ubc95 - \\"\uc0c1\ud0dc\uad00\ub9ac\ub97c \ud558\uc9c0 \uc54a\ub294\ub2e4\\" (feat. overlay-kit)","tags":["frontend","state management","overlay-kit"]},"unlisted":false,"nextItem":{"title":"\ub3c4\ub85c\uba85\uc8fc\uc18c\uc640 \uc9c0\ubc88\uc8fc\uc18c\uc5d0\uc11c \uc0c1\uc138\uc8fc\uc18c \uc81c\uac70\ud558\ub294 \ubc95","permalink":"/blog/\uc0c1\uc138\uc8fc\uc18c-\uc5c6\uc560\uae30"}},"content":"\ud504\ub85c\uadf8\ub798\ubc0d\uc5d0\uc11c\uc758 \ub9ce\uc740 \ubb38\uc81c\ub4e4\uc774 \uadf8\ub7ec\ud558\ub4ef\uc774 \uc0c1\ud0dc\uad00\ub9ac\ub294 \uc815\ub2f5\uc774 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\uc0c1\ud0dc\uad00\ub9ac\uc758 \ud615\ud0dc\ub294 \uc0ac\uc6a9\ud558\ub294 \ub77c\uc774\ube0c\ub7ec\ub9ac\uac00 \ubb34\uc5c7\uc774\ub0d0, \uc5b4\ub5a4 \ucee8\ubca4\uc158\uc5d0 \ub530\ub77c \uc815\uc758\ud558\ub290\ub0d0, \ub4f1 \uc870\uc9c1\ub9c8\ub2e4 \uad49\uc7a5\ud788 \uad6c\uccb4\uc801\uc778 \ucc28\uc774\ub4e4\uc774 \uc788\uc744 \uac83\uc785\ub2c8\ub2e4.\\n\\n\uadf8\ub7ec\ub2c8 \uc5b4\ub5bb\uac8c \ud558\ub294 \uac83\uc774 \\"\uc88b\uc740\\" \uc0c1\ud0dc\uad00\ub9ac\ub0d0? \ub77c\uace0 \ubb3b\ub294\ub2e4\uba74 \uc81c\uac01\uac01\uc758 \ub300\ub2f5\uc774 \ub098\uc62c \uc218\ubc16\uc5d0 \uc5c6\uc9c0 \uc54a\uc744\uae4c \uc0dd\uac01\ud569\ub2c8\ub2e4.\\n\\n\ud558\uace0 \uc788\ub294 \uace0\ubbfc\uacfc, \uacaa\uace0 \uc788\ub294 \ubb38\uc81c\uac00 \ub2e4\ub974\ub2c8 \uc88b\uc740 \uac83\uc5d0 \ub300\ud55c \uc815\uc758\uac00 \uc11c\ub85c \ub2ec\ub77c\uc9c0\ub294 \uac83\uc774\uc9c0\uc694.\\n\\n\uc0c1\ud0dc\ub97c \ud558\ub098 \ucd94\uac00\ud55c\ub2e4\uace0 \uc0dd\uac01\ud574 \ubcf4\uaca0\uc2b5\ub2c8\ub2e4. \uc77c\ubc18\uc801\uc73c\ub85c\ub294 \uc544\ub798\uc640 \uac19\uc740 \uace0\ubbfc\ub4e4\uc774 \uc788\uc744 \uac83\uc785\ub2c8\ub2e4.\\n\\n- \uc5b4\ub5a4 \ud30c\uc77c\uc5d0, \ubb34\uc5c7\ub4e4\uacfc \ud568\uaed8 \ub458 \uac83\uc778\uac00?\\n- \ub514\ub809\ud130\ub9ac\ub294 \uc5b4\ub5bb\uac8c \uad00\ub9ac\ud560 \uac83\uc778\uac00?\\n\\n\ub2e8\uc21c\ud788 \uc0c1\ud0dc \ud558\ub098\ub97c \ucd94\uac00\ud558\ub824\uace0 \ud574\ub3c4 \uc0dd\uac01\ud574\uc57c \ud560 \uac83\ub4e4\uc774 \uc0c1\ub2f9\ud788 \ub9ce\uc2b5\ub2c8\ub2e4.\\n\\n\uc989, \uc0c1\ud0dc\uad00\ub9ac\ub294 \ub9ce\uc740 \uace0\ubbfc\uacfc \ud310\ub2e8\uc744 \uc694\uad6c\ud569\ub2c8\ub2e4. \uadf8\ub7ec\ub2c8 \uc0c1\ud0dc\uad00\ub9ac\uac00 \ud53c\uace4\ud560 \uc218\ubc16\uc5d0 \uc5c6\uc2b5\ub2c8\ub2e4.\\n\\n\uc774\ub7ec\ud55c \uc0c1\ud0dc\uad00\ub9ac\uc758 \ud53c\ub85c\ub97c \uc904\uc5ec\uc8fc\ub294 \uc720\uc6a9\ud55c \uc624\ud508\uc18c\uc2a4\ub97c \uc18c\uac1c\ud558\ub824\uace0 \ud558\ub294\ub370\uc694.\\n\ubc14\ub85c [overlay-kit](https://overlay-kit.slash.page/ko)\uc785\ub2c8\ub2e4.\\n\\n---\\n\\n\uc0dd\uac01\ud574\ubcf4\uba74 \uc6b0\ub9ac\uac00 \ub2e4\ub8e8\ub294 \uc0c1\ud0dc\ub4e4\uc740 \uc624\ubc84\ub808\uc774\uc758 \uc0c1\ud0dc\uc778 \uacbd\uc6b0\uac00 \uaf64 \ub9ce\uc2b5\ub2c8\ub2e4.\\noverlay-kit\uc740 React\uc5d0\uc11c \uc624\ubc84\ub808\uc774\ub4e4\uc758 \uc0c1\ud0dc\ub97c \uac04\ud3b8\ud558\uac8c \ub2e4\ub8e8\ub294\ub370 \ud070 \ub3c4\uc6c0\uc744 \uc90d\ub2c8\ub2e4.\\n\\n\uc608\uc2dc \ucf54\ub4dc\uc640 \ud568\uaed8 overlay-kit\uc774 \ud574\uacb0\ud558\ub294 \ubb38\uc81c\uc5d0 \ub300\ud574 \uc774\uc57c\uae30\ud574 \ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n```tsx\\nexport const SimpleExample = () => {\\n  const [isOpen, setIsOpen] = useState(false);\\n\\n  const openOverlay = () => {\\n    setIsOpen(true);\\n  };\\n\\n  const close = () => {\\n    setIsOpen(false);\\n  };\\n\\n  return (\\n    <>\\n      <button onClick={openOverlay}>Open with useState</button>\\n      <Dialog opened={isOpen}>\\n        <span>Overlay Created: </span>\\n        <Button onClick={close}>Close</Button>\\n      </Dialog>\\n    </>\\n  );\\n};\\n```\\n\\n\uc704 \ucf54\ub4dc\ub294 `useState`\ub97c \uc0ac\uc6a9\ud55c `<Dialog />`\ub97c \uc5f4\uace0 \ub2eb\uae30 \uc704\ud55c \uac04\ub2e8\ud55c \uc5d0\uc81c\uc785\ub2c8\ub2e4. (`useState`\\n\uac00 \uc544\ub2cc \ubcc4\ub3c4\uc758 \uc0c1\ud0dc \uad00\ub9ac \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uc0ac\uc6a9\ud558\ub354\ub77c\ub3c4 \uc138\ubd80 \ud615\ud0dc\ub294 \ub2e4\ub97c\uc9c0\ub77c\ub3c4 \ubcf8\uc9c8\uc801\uc73c\ub85c\ub294 \uac19\uc740 \uc77c\uc744 \ud558\ub294 \ucf54\ub4dc\ub97c \uc791\uc131\ud558\uac8c \ub420 \uac83\uc774\ub77c \uc0dd\uac01\ud569\ub2c8\ub2e4.)\\n\\n\uc704 \uc608\uc81c\ub294 \uc544\ub798\uc640 \uac19\uc740 \uc544\uc26c\uc6b4 \ubd80\ubd84\ub4e4\uc774 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n1. `isOpen` \uc0c1\ud0dc\ub97c \uc5b4\ub5bb\uac8c \uad00\ub9ac\ud560 \uac83\uc774\ub0d0\uc758 \ubb38\uc81c\\n\\n- \uc55e\uc120 \ub0b4\uc6a9\ucc98\ub7fc \ud558\ub098\uc758 \uc0c1\ud0dc\ub97c \uc815\uc758\ud558\ub294 \uac83\uc740 \ub9ce\uc740 \uace0\ubbfc\uc774 \ub530\ub77c\uc635\ub2c8\ub2e4.\\n\\n2. \uc5f4\uae30, \ub2eb\uae30 \uc774\ubca4\ud2b8\uc758 \ubc88\uac70\ub85c\uc6b4 \ud578\ub4e4\ub9c1\\n\\n- \uc774\ub7f0 \uc5f4\uace0 \ub2eb\ud788\ub294 \uc0c1\ud0dc\ub97c \uac16\ub294 \ucef4\ud3ec\ub10c\ud2b8\ub4e4\uc740 \uad49\uc7a5\ud788 \ub9ce\uc2b5\ub2c8\ub2e4. \uadf8 \ub54c \ub9c8\ub2e4 \uc0c1\ud0dc\ub97c \ud558\ub098\uc529 \ub9cc\ub4e4\uace0, \uac01\uac01\uc5d0 \ub300\ud574\uc11c \uc5f4\uae30, \ub2eb\uae30 \uc774\ubca4\ud2b8\ub97c \uc815\uc758\ud558\ub294 \uc77c\uc740 \ubc88\uac70\ub85c\uc6b4 \uc77c\uc785\ub2c8\ub2e4.\\n\\n3. `<Dialog />` \ucef4\ud3ec\ub10c\ud2b8\uc758 \ud2b8\ub9ac\uc0c1 \uc704\uacc4\\n\\n- `<Dialog />`\ub97c \ub2e4\ub978 \ucef4\ud3ec\ub10c\ud2b8\uc5d0\uc11c\ub3c4 \uc5f4\uace0 \uc2f6\ub2e4\uace0 \ud574\ubcf4\uaca0\uc2b5\ub2c8\ub2e4. \uadf8\ub807\ub2e4\uba74 \uc774 `<Dialog />` \ucef4\ud3ec\ub10c\ud2b8\ub294 \uacf5\ud1b5 \ubd80\ubaa8\uc5d0 \uc704\uce58\uc2dc\ud0a4\ub294 \ub4f1 \ucef4\ud3ec\ub10c\ud2b8\uc758 \uc704\uacc4 \uad00\ub9ac\uac00 \ud544\uc694\ud569\ub2c8\ub2e4.\\n\\n---\\n\\n\ub2e4\uc74c\uc740 overlay-kit\uc744 \uc0ac\uc6a9\ud55c \uc608\uc81c\uc785\ub2c8\ub2e4.\\n\\n```tsx\\nexport const OverlayKitExample = () => {\\n  const openOverlay = () => {\\n    overlay.open(({ isOpen, close }) => (\\n      <Dialog opened={isOpen}>\\n        <span>Overlay Created: </span>\\n        <Button onClick={close}>Close</Button>\\n      </Dialog>\\n    ));\\n  };\\n\\n  return <button onClick={openOverlay}>Open with overlay-kit</button>;\\n};\\n```\\n\\n\uc55e\uc11c \uc5b8\uae09\ud55c \ubaa8\ub4e0 \uace0\ubbfc\ub4e4\uc744 \ud55c \ubc88\uc5d0 \ud574\uacb0\ud55c \ubaa8\uc2b5\uc785\ub2c8\ub2e4.\\n\\n\uc7ac\ubc0c\ub294 \uc810\uc740 \uc0c1\ud0dc\ub97c \uac1c\ubc1c\uc790\uac00 \uc9c1\uc811 \uad00\ub9ac\ud558\uc9c0 \uc54a\uc74c\uc73c\ub85c\uc368 \ub9ce\uc740 \ubb38\uc81c\uac00 \ud574\uacb0\ub410\ub2e4\ub294 \uc810\uc785\ub2c8\ub2e4.\\n\\n\uac1c\uc778\uc801\uc73c\ub85c\ub294 `<Dialog />` \ucef4\ud3ec\ub10c\ud2b8\uac00 \ud2b8\ub9ac\uc758 \uc5b4\ub290 \ubd80\ubd84\uc5d0 \uc704\uce58\ud560\uc9c0\ub97c \uace0\ubbfc\ud558\uc9c0 \uc54a\uc544\ub3c4 \ub418\ub294 \uc810\uc774 \uad49\uc7a5\ud788 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc774 \ucef4\ud3ec\ub10c\ud2b8\ub294 `openOverlay()` \ud568\uc218\ub85c \uaca9\ub9ac\ub418\uc5c8\uace0, \ub2e4\ub978 \uacf3\uc5d0\uc11c \uc5f4\uace0 \uc2f6\ub2e4\uba74 \uadf8\ub0e5 \uc774 \ud568\uc218\ub97c export \ud574\uc11c \uc0ac\uc6a9\ud558\uba74 \ub429\ub2c8\ub2e4.\\n\\n`overlay-kit`\uc758 \uc18c\uc2a4\ub97c \ub4e4\uc5ec\ub2e4 \ubcf4\uae30\ub3c4 \ud588\ub294\ub370, \uc0ac\uc2e4 \ub9c8\ubc95\uac19\uc774 \ud574\uacb0\ub418\ub294 \uac83\uc740 \uc5c6\uace0 \uc774\uc57c\uae30\ud55c \ubaa8\ub4e0 \ubb38\uc81c\ub4e4\uc740 `overlay-kit` \ub0b4\ubd80\uc801\uc73c\ub85c \ub2e4 \ucc98\ub9ac\ud558\uace0 \uc788\uae30\uc5d0 \uc6b0\ub9ac\ub294 \uadf8\ub0e5 \uc798 \uc0ac\uc6a9\ud558\uba74 \ub418\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n\ud68c\uc0ac\uc5d0\uc11c \ub3d9\ub8cc \ubd84\uc774 \uc81c\uc548\ud574\uc8fc\uc154\uc11c \ub3c4\uc785\ud574\ubcf4\uace0, \uc800\ud76c \ucf54\ub4dc\ubca0\uc774\uc2a4\uc5d0 \ub9de\uac8c \uc218\uc815\ud574\uc11c \uc0ac\uc6a9\ud558\uace0 \uc788\ub294\ub370 \ub108\ubb34 \ub9cc\uc871\uc2a4\ub7fd\ub124\uc694.\\n\\n\uba38\ub9ac \uc544\ud508 \uc0c1\ud0dc\uad00\ub9ac\ub294 overlay-kit\uc5d0 \uc544\uc6c3\uc18c\uc2f1\ud558\uace0 \uc6b0\ub9ac\ub294 \ub354 \uc911\uc694\ud55c \ubb38\uc81c\ub97c \ud478\ub294\ub370 \uc2dc\uac04\uc744 \uc4f8 \uc218 \uc788\uaca0\uc2b5\ub2c8\ub2e4."},{"id":"\uc0c1\uc138\uc8fc\uc18c-\uc5c6\uc560\uae30","metadata":{"permalink":"/blog/\uc0c1\uc138\uc8fc\uc18c-\uc5c6\uc560\uae30","source":"@site/blog/2025-02-07-delete-detail-address.mdx","title":"\ub3c4\ub85c\uba85\uc8fc\uc18c\uc640 \uc9c0\ubc88\uc8fc\uc18c\uc5d0\uc11c \uc0c1\uc138\uc8fc\uc18c \uc81c\uac70\ud558\ub294 \ubc95","description":"\uc774\uac74 \ub0b4\ube44\uac8c\uc774\uc158\uc5d0 \ud544\uc694 \uc5c6\uc5b4\uc694","date":"2025-02-07T00:00:00.000Z","formattedDate":"2025\ub144 2\uc6d4 7\uc77c","tags":[{"label":"programming","permalink":"/blog/tags/programming"},{"label":"map","permalink":"/blog/tags/map"}],"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"\uc0c1\uc138\uc8fc\uc18c-\uc5c6\uc560\uae30","title":"\ub3c4\ub85c\uba85\uc8fc\uc18c\uc640 \uc9c0\ubc88\uc8fc\uc18c\uc5d0\uc11c \uc0c1\uc138\uc8fc\uc18c \uc81c\uac70\ud558\ub294 \ubc95","tags":["programming","map"]},"unlisted":false,"prevItem":{"title":"\ud504\ub860\ud2b8\uc5d4\ub4dc \uc0c1\ud0dc\uad00\ub9ac \uc798 \ud558\ub294 \ubc29\ubc95 - \\"\uc0c1\ud0dc\uad00\ub9ac\ub97c \ud558\uc9c0 \uc54a\ub294\ub2e4\\" (feat. overlay-kit)","permalink":"/blog/overlay-kit"},"nextItem":{"title":"\ub0b4\uac00 2023\ub144\uc5d0 \ubc30\uc6b4 \uac83\ub4e4","permalink":"/blog/2023-retro"}},"content":"## \uc774\uac74 \ub0b4\ube44\uac8c\uc774\uc158\uc5d0 \ud544\uc694 \uc5c6\uc5b4\uc694\\n\\n\uc5c5\ubb34\ud558\uba74\uc11c \ub3c4\ub85c\uba85\uacfc \uc9c0\ubc88\uc8fc\uc18c\uac00 \ud63c\uc7ac\ub418\uc5b4 \uc788\ub294 \ub370\uc774\ud130\ub97c \ub2e4\ub8ec \uc801\uc774 \uc788\ub294\ub370, \uc608\ub97c \ub4e4\uba74 \uc774\ub7f0 \ub370\uc774\ud130\uc600\uc2b5\ub2c8\ub2e4.\\n\\n- \uc8fc\uc18c1: \uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uc11c\ucd08\uad6c \ubc18\ud3ec\ub3d9 20-43 \ubc18\ud3ec\uc790\uc774\uc544\ud30c\ud2b8 101\ub3d9 101\ud638\\n- \uc8fc\uc18c2: \uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uc601\ub4f1\ud3ec\uad6c \uad6d\ud68c\ub300\ub85c62\uae38 9 (\uc5ec\uc758\ub3c4\ub3d9)\\n\\n\uc608\uc2dc\uc758 \uc8fc\uc18c 1\uc758 \uacbd\uc6b0 \uc9c0\ubc88\uc8fc\uc18c, \uc8fc\uc18c2\ub294 \ub3c4\ub85c\uba85\uc8fc\uc18c \uc785\ub2c8\ub2e4.\\n\\n\ubb38\uc81c\ub294 \uc774 \ub370\uc774\ud130\ub97c \ud1a0\ub300\ub85c \ub0b4\ube44\uac8c\uc774\uc158 API\uc5d0 \uc694\uccad\uc744 \ubcf4\ub0b4\uc57c \ud588\ub294\ub370, \uc800\ud76c\uac00 \uc0ac\uc6a9 \uc911\uc778 API\uc5d0\uc11c\ub294 \uc0c1\uc138 \uc8fc\uc18c\uc5d0 \ub300\ud55c \ucc98\ub9ac\uac00 \ub418\uc5b4 \uc788\uc9c0 \uc54a\uc544\uc11c \uac04\ud639 \uc751\ub2f5\uc744 \ub0b4\ub9ac\uc9c0 \uc54a\ub294 \uacbd\uc6b0\uac00 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uc8fc\uc18c 3\uc744 \uc608\ub85c \ub4e4\uba74 \uc0c1\uc138\uc8fc\uc18c\uc5d0 \ud574\ub2f9\ud558\ub294 `\ubc18\ud3ec\uc790\uc774\uc544\ud30c\ud2b8 101\ub3d9 101\ud638`\uc5d0\uc11c `101\ud638`\ub294 \uc0ac\uc2e4 \uae38\ucc3e\uae30\uc5d0 \ud544\uc694\ud55c \uc815\ubcf4\ub294 \uc544\ub2d9\ub2c8\ub2e4. \uc774\uac78 \ud3ec\ud568\uc2dc\ucf1c\uc11c \uc694\uccad\ud558\uba74 \uae38\uc744 \uc81c\ub300\ub85c \ucc3e\uc9c0 \ubabb\ud558\ub354\ub77c\uad6c\uc694.\\n\\n\uc774\ud574\ub97c \ub3d5\uae30 \uc704\ud574 \uc8fc\uc18c\ub97c \ub450 \uac00\uc9c0\ub85c \ubd84\ub958\ud574 \ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n1. \ubc30\uc1a1\uc744 \uc704\ud55c \uc8fc\uc18c\\n2. \uae38\ucc3e\uae30\ub97c \uc704\ud55c \uc8fc\uc18c\\n\\n1\uacfc 2\uac00 \uac19\uc740 \uacbd\uc6b0\ub3c4 \ubd84\uba85 \uc788\uc9c0\ub9cc, \uacf5\ub3d9\uc8fc\ud0dd \ub4f1\uc758 \uacbd\uc6b0 \uc815\ud655\ud55c \ud638\uc218\ub97c \ucc3e\uae30 \uc704\ud55c \uc0c1\uc138\uc8fc\uc18c\uac00 \ub354\ud574\uc9c0\uae30 \ub54c\ubb38\uc5d0 \ud45c\uae30\uc5d0 \ucc28\uc774\uac00 \uc0dd\uae41\ub2c8\ub2e4.\\n\\n\uc81c\uac00 \uacaa\uc740 \ubb38\uc81c\uc758 \uacbd\uc6b0 2\ub9cc \ucde8\uae09\ud558\ub294 API\uc5d0 1\uc744 \ubcf4\ub0c8\uae30 \ub54c\ubb38\uc5d0 \ubc1c\uc0dd\ud55c \ubb38\uc81c\uc600\uc2b5\ub2c8\ub2e4.\\n\\n\uadf8\ub807\ub2e4\uba74 \ubb38\uc790\uc5f4 \ub4b7\ubd80\ubd84\uc758 \uc0c1\uc138\uc8fc\uc18c\ub9cc \uc81c\uac70\ud574\uc11c \uc694\uccad\ud558\uba74 \ub418\uaca0\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4ed\ub2c8\ub2e4. \uc774\uc81c \uc774 \ubb38\uc790\uc5f4\ub4e4\uc758 \uc5b4\ub514\uc11c\ubd80\ud130\uac00 \uc0c1\uc138\uc8fc\uc18c\uc778\uc9c0\ub97c \ucc3e\uc544\uc57c \ud569\ub2c8\ub2e4.\\n\\n\uadf8\ub807\ub2e4\uba74 \uc11e\uc5ec \uc788\ub294 \ub3c4\ub85c\uba85\uc8fc\uc18c\uc640 \uc9c0\ubc88\uc8fc\uc18c\ub97c \uac01\uac01 \ub2e4\ub974\uac8c \ud30c\uc2f1\ud574\uc57c \ud560\uae4c\uc694? \uc560\ucd08\uc5d0 \ubcc4\ub3c4\uc758 \uce7c\ub7fc\uc774 \uc5c6\ub294\ub370 \ubb38\uc790\uc5f4\ub9cc \ubcf4\uace0 \uc774 \ub458\uc744 \uad6c\ubd84\ud560 \uc218\ub294 \uc788\uc744\uae4c\uc694?\\n\\n---\\n\\n## \ubaa8\ub974\uaca0\uc744 \ub550 \ubb38\uc11c\ub97c \ubcf4\uae30\\n\\n[\ub3c4\ub85c\uba85\uc8fc\uc18c \uc548\ub0b4 \uc2dc\uc2a4\ud15c](https://www.juso.go.kr/openIndexPage.do)\uc5d0 \uac00\ubcf4\uba74 \ub3c4\ub85c\uba85 \uc8fc\uc18c \uc18c\uac1c pdf \ud30c\uc77c\uc744 \ubc30\ud3ec\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n![Example banner](/img/address-comparison.png)\\n\\n\ub208\uc5ec\uaca8\ubcf4\uc544\uc57c \ud560 \ubd80\ubd84\uc740 \uac00\uc6b4\ub370\uc758 `\ub3d9/\ub9ac \uc9c0\ubc88`\uc774 `\ub3c4\ub85c\uba85 \uac74\ubb3c\ubc88\ud638`\uc5d0 \ub300\uc751\ub41c\ub2e4\ub294 \uc810\uc785\ub2c8\ub2e4. \uc790\uc138\ud55c `\ub3c4\ub85c\uba85 \uac74\ubb3c\ubc88\ud638` \uaddc\uce59\uc740 \uc544\ub798\uc640 \uac19\uc740\ub370\uc694.\\n\\n![Example banner](/img/road-address.png)\\n\\n\uc774 \ubd80\ubd84\uc774 \uc911\uc694\ud569\ub2c8\ub2e4. \ub3c4\ub85c\uba85\uc740 \ubd99\uc5ec \uc4f0\uace0, \ub3c4\ub85c\uba85\uacfc \uac74\ubb3c\ubc88\ud638\ub294 \ub744\uc6b0\uae30 \ub54c\ubb38\uc5d0 \uc774\ub294 \uae30\uc874\uc758 `\ub3d9/\ub9ac \uc9c0\ubc88`\uacfc \uacf5\ubc31\uc744 \uae30\uc900\uc73c\ub85c \ud638\ud658\uc774 \ub41c\ub2e4\uace0 \ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\ubb34\uc2a8 \ub9d0\uc778\uc9c0 \uc55e\uc11c \ubcf4\uc558\ub358 \uc608\uc2dc\ub97c \ub2e4\uc2dc \ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n- \uc8fc\uc18c1: \uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uc11c\ucd08\uad6c **\ubc18\ud3ec\ub3d9 20-43** \ubc18\ud3ec\uc790\uc774\uc544\ud30c\ud2b8 101\ub3d9 101\ud638\\n- \uc8fc\uc18c2: \uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uc601\ub4f1\ud3ec\uad6c **\uad6d\ud68c\ub300\ub85c62\uae38 9** (\uc5ec\uc758\ub3c4\ub3d9)\\n\\n\uc9c0\ubc88\uc8fc\uc18c\uc758 `\ub3d9/\ub9ac \uc9c0\ubc88`\uc740 `\ubc18\ud3ec\ub3d9 20-43`, \ub3c4\ub85c\uba85\uc8fc\uc18c\uc758 `\ub3c4\ub85c\uba85 \uac74\ubb3c\ubc88\ud638`\ub294 `\uad6d\ud68c\ub300\ub85c62\uae38 9`\uc5d0 \ud574\ub2f9\ud558\ub294 \uac83\uc744 \uc54c \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \uc774 \uc8fc\uc18c\ub4e4\uc740 \ubaa8\ub450 \uac00\uc6b4\ub370\uc5d0 \uacf5\ubc31 \ubb38\uc790\ub97c 1\uac1c \ud3ec\ud568\ud55c \ubb38\uc790\uc5f4\uc785\ub2c8\ub2e4.\\n\\n\uc774 \uc8fc\uc18c\ub4e4 \uc774\ud6c4\ubd80\ud130\ub294 \uc0c1\uc138\uc8fc\uc18c\uac00 \ub098\uc624\ub294 \uac83\uc744 \ud655\uc778\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\uc989, \ub3c4\ub85c\uba85\uc774\ub0d0 \uc9c0\ubc88\uc774\ub0d0\uc5d0 \uad00\uacc4 \uc5c6\uc774 \ub2e8\uc21c\ud788 \ubb38\uc790\uc5f4\uc744 \uacf5\ubc31\uc744 \uae30\uc900\uc73c\ub85c \uc55e\uc5d0\uc11c\ubd80\ud130 4\ubc88 \ucde8\ud558\uba74 \uc0c1\uc138\uc8fc\uc18c\ub97c \uc81c\uac70\ud55c \uc8fc\uc18c\ub97c \uc5bb\uc744 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n```js\\n\\"\uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uc11c\ucd08\uad6c \ubc18\ud3ec\ub3d9 20-43 \ubc18\ud3ec\uc790\uc774\uc544\ud30c\ud2b8 101\ub3d9 101\ud638\\"\\n  .split(\\" \\")\\n  .slice(0, 4)\\n  .join(\\" \\");\\n// \'\uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uc11c\ucd08\uad6c \ubc18\ud3ec\ub3d9 20-43\'\\n\\n\\"\uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uc601\ub4f1\ud3ec\uad6c \uad6d\ud68c\ub300\ub85c62\uae38 9 (\uc5ec\uc758\ub3c4\ub3d9)\\"\\n  .split(\\" \\")\\n  .slice(0, 4)\\n  .join(\\" \\");\\n// \'\uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uc601\ub4f1\ud3ec\uad6c \uad6d\ud68c\ub300\ub85c62\uae38 9\'\\n```\\n\\n\uc774\uc81c \uc774 \ubb38\uc790\uc5f4\uc774 \uc2e0\uc8fc\uc18c\uc778\uc9c0 \uad6c\uc8fc\uc18c\uc778\uc9c0\uc640\ub294 \uad00\uacc4 \uc5c6\uc774 \uac04\ub2e8\ud55c \ubc29\ubc95\uc73c\ub85c \uc0c1\uc138\uc8fc\uc18c\ub97c \uc81c\uac70\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4."},{"id":"2023-retro","metadata":{"permalink":"/blog/2023-retro","source":"@site/blog/2024-02-19-2023-retro.md","title":"\ub0b4\uac00 2023\ub144\uc5d0 \ubc30\uc6b4 \uac83\ub4e4","description":"- \uc870\uae08 \ub354 common\ud55c \ucf54\ub4dc \ubca0\uc774\uc2a4\uc5d0 \uae30\uc5ec\ud558\uae30 / \ubaa9\uc801 \uc870\uc9c1\uc5d0\uc11c \uc77c\ud558\uae30","date":"2024-02-19T00:00:00.000Z","formattedDate":"2024\ub144 2\uc6d4 19\uc77c","tags":[{"label":"career","permalink":"/blog/tags/career"},{"label":"retrospect","permalink":"/blog/tags/retrospect"}],"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"2023-retro","title":"\ub0b4\uac00 2023\ub144\uc5d0 \ubc30\uc6b4 \uac83\ub4e4","tags":["career","retrospect"]},"unlisted":false,"prevItem":{"title":"\ub3c4\ub85c\uba85\uc8fc\uc18c\uc640 \uc9c0\ubc88\uc8fc\uc18c\uc5d0\uc11c \uc0c1\uc138\uc8fc\uc18c \uc81c\uac70\ud558\ub294 \ubc95","permalink":"/blog/\uc0c1\uc138\uc8fc\uc18c-\uc5c6\uc560\uae30"},"nextItem":{"title":"redux-saga\uc640 \ud5e4\uc5b4\uc9c8 \uacb0\uc2ec","permalink":"/blog/good-bye-saga"}},"content":"- \uc870\uae08 \ub354 common\ud55c \ucf54\ub4dc \ubca0\uc774\uc2a4\uc5d0 \uae30\uc5ec\ud558\uae30 / \ubaa9\uc801 \uc870\uc9c1\uc5d0\uc11c \uc77c\ud558\uae30\\n\\n\uc6f9 \ucf54\ub4dc \ubca0\uc774\uc2a4\uc5d0 \ub300\ud55c \uc774\ud574\ub3c4\uac00 \ub298\uc5b4\ub098\uace0 shared, common \uc815\ub3c4\ub85c \ud45c\ud604\ub418\ub294 \uacf5\ud1b5 \ubaa8\ub4c8\uc5d0 \ub300\ud55c \uae30\uc5ec\ub3c4 \uc790\uc5f0\uc2a4\ub808 \ub298\uc5b4\ub0ac\ub2e4. \ubaa9\uc801 \uc870\uc9c1\uc5d0\uc11c \uba87 \uac1c\uc6d4\uac04 \uc77c\ud558\uae30\ub3c4 \ud588\ub294\ub370 \uc791\ub144 \ud55c \ud574 \uac00\uc7a5 \ubab0\uc785\ud574\uc11c \uc77c\ud588\ub358 \uacbd\ud5d8\uc73c\ub85c \uae30\uc5b5\ub410\ub2e4. \ub0b4\uac00 \ub9e1\uc740 \ubd80\ubd84\uc774 \uc544\ub2c8\ub354\ub77c\ub3c4 \uc804\uccb4 \uc81c\ud488\uc5d0 \ub300\ud55c \ub9e5\ub77d\uc744 \uace0\ubbfc\ud558\ub294 \uacbd\ud5d8\uc774 \uc88b\uc558\ub2e4. \uc790\uc5f0\uc2a4\ub808 \uc5c5\ubb34\uc2dc\uac04 \ub0b4 \ud68c\uc758\uc640 \ub17c\uc758\ud558\ub294 \uc2dc\uac04\uc740 \uc0c1\ub300\uc801\uc73c\ub85c \ub298\uc5b4\ub098\uace0 \ucf54\ub4dc\ub97c \uc9d1\uc911\ud574\uc11c \uc791\uc5c5\ud558\ub294 \uc2dc\uac04\uc740 \uc904\uc5c8\ub358 \uc810\uc740 \uc544\uc27d\ub2e4.\\n\\n---\\n\\n- \ud76c\ub9dd \ud1f4\uc9c1\uacfc \uc778\uc218\ud569\ubcd1 \uc774\uc288\\n\\n\ud68c\uc0ac\uac00 \uacbd\uc601\uc0c1 \uc5b4\ub824\uc6c0\uc73c\ub85c \uc778\ud574 \uc778\uc6d0\uc744 \uc808\ubc18 \uac00\ub7c9 \uac10\ucd95\ud588\ub2e4. \ub098\ub3c4 \ud68c\uc0ac\ub97c \uacc4\uc18d \ub2e4\ub2d0\uc9c0, \ud639\uc740 \ub2e4\ub2d0 \uc218 \uc788\uc744\uc9c0 \uac71\uc815\ud558\uace0 \uace0\ubbfc\ud558\ub290\ub77c \uc2ec\uc801\uc73c\ub85c \uc5b4\ub824\uc6e0\ub2e4. \uacb0\uacfc\uc801\uc73c\ub85c\ub294 \uacc4\uc18d \ud68c\uc0ac\ub97c \ub2e4\ub2c8\uace0 \uc788\ub2e4\\n\\n\ud68c\uc0ac\uac00 \uac10\uc815\uc801\uc73c\ub85c \uaca9\uc815\uc801\uc778 \uc2dc\uc98c\uc774\uc5c8\ub2e4. \ub5a0\ub098\uae30\ub85c \ub9c8\uc74c \uba39\uc740 \ubd84\ub4e4\uc740 \uadf8 \uc790\ub9ac\uc5d0\uc11c \ubc14\ub85c \uc9d0\uc744 \uc2f8\uace0 \ub098\uac00\uc168\ub2e4. \ud568\uaed8 \uc77c\ud558\ub358 \ub3d9\ub8cc\ub4e4\uc744 \ud558\ub8e8\uc544\uce68\uc5d0 \ub5a0\ub098\ubcf4\ub0b4\ub294 \uc77c\uc774 \uc27d\uc9c0 \uc54a\uc558\ub2e4. \ud76c\ub9dd\ud1f4\uc9c1 \uc774\ud6c4\ub85c\ub3c4 \ud68c\uc0ac\uc758 \uc778\uc218/\ud569\ubcd1 \uc774\uc288 \ub4f1\uc774 \ubc18\ubcf5\ub418\uc5c8\ub2e4. \uac70\ucde8\uc758 \ubd88\ud655\uc2e4\uc131\uc744 \uc9c0\ub2cc \ucc44\ub85c \uc77c\ud558\ub294 \uac83\uc774 \uc9c1\uc6d0\uc73c\ub85c\uc11c \ubd88\uc548\uc815\ud558\uac8c \ub290\uaef4\uc9c4 \ud55c \ud574\uc600\ub358 \uac83 \uac19\ub2e4.\\n\\n\ubc30\uc6b4 \uc810\uc774 \uc788\ub2e4\uba74 \uc774\ub7ec\ud55c \ubcc0\ud654 \uc870\ucc28\ub3c4 \ubc84\ud168\ub0bc \uc218 \uc788\ub294 \uc5b4\ub824\uc6c0\uc774\ub77c\ub294 \uac83\uc744 \uc54c\uc558\ub2e4\ub294 \uc810\uc774\ub2e4. \uadf8\ub7f0 \uc0c1\ud669\uc5d0\uc11c \uc2a4\ud2b8\ub808\uc2a4\ub97c \ubc1b\ub294 \uac83\uc740 \ud53c\ud560 \uc218 \uc5c6\uc5c8\uc9c0\ub9cc \ud560 \uc77c\uc744 \ud558\ub2e4\ubcf4\ub2c8 \uc5b4\ub5bb\uac8c\ub4e0 \ubc84\ud168\ub0c8\ub2e4.\\n\\n\ub2f9\uc5f0\ud55c \uc0ac\uc2e4\uc774\uc9c0\ub9cc \ud68c\uc0ac\uc640 \uac1c\uc778\uc740 \uacc4\uc57d \uad00\uacc4\uc774\uace0, \uadf8 \uacc4\uc57d\uc740 \uc0dd\uac01\ubcf4\ub2e4 \uacac\uace0\ud558\uc9c0 \uc54a\uc744 \uc218\ub3c4 \uc788\uaca0\ub2e4\ub294 \uc0dd\uac01\ub3c4 \ub4e4\uc5c8\ub2e4. \uac1c\uc778\uc73c\ub85c\uc11c \ub354 \uae38\uac8c \uc774\uc5b4\uc9c8 \ub0b4 \uc0b6\uc744 \uc798 \uac1c\ubc1c\ud558\uae30 \uc704\ud574 \ub178\ub825\ud574\uc57c\uaca0\ub2e4\ub294 \uc0dd\uac01\ub3c4 \ub4e4\uc5c8\ub2e4.\\n\\n\uc5b4\ub5a4 \uc120\ud0dd\uc744 \ud558\uc168\ub358\uc9c0 \ud568\uaed8 \uc5b4\ub824\uc6b4 \uc77c\uc744 \uacaa\uc740 \ub3d9\ub8cc \ubd84\ub4e4\uc774 \ub2e4\ub4e4 \uc798 \uc9c0\ub0b4\uc168\uc73c\uba74 \uc88b\uaca0\ub2e4.\\n\\n---\\n\\n- \ud63c\uc790 \uac1c\ubc1c\ud558\uae30\\n\\n\ud76c\ub9dd \ud1f4\uc9c1\uc73c\ub85c \uc778\ud574 \uc6f9 \ud504\ub860\ud2b8\uc5d4\ub4dc\ud300 \ud300\uc6d0\ub4e4\uc774 \ubaa8\ub450 \ub5a0\ub0ac\ub2e4. \ud63c\uc790\uc11c \uc798 \ud574\ub0bc \uc218 \uc788\uc744\uc9c0 \uac71\uc815\uc774 \ub9ce\uc558\ub2e4. \uac00\uc7a5 \uc624\ub798 \uc2dc\uac04\uc744 \ubcf4\ub0b8 \ud300 \ub3d9\ub8cc\ub4e4\uc774 \ub5a0\ub098\ub2c8 \uc77c\uc801\uc778 \uba74\uc744 \ub5a0\ub098\uc11c \uc2ec\uc801\uc73c\ub85c\ub3c4 \uc5b4\ub824\uc6e0\ub2e4.\\n\\n\ud640\ub85c \uc81c\ud488 \uac1c\ubc1c\uc5d0 \ud22c\uc785\ub418\ub2c8 \uc2ec\ub9ac\uc801 \uc555\ubc15\uc744 \ub290\ub07c\ub294 \uacbd\uc6b0\uac00 \uc885\uc885 \uc788\uc5c8\ub2e4. \uc5b4\ub5a4 \ubb38\uc81c\uac00 \uc798 \ud480\ub9ac\uc9c0 \uc54a\uc744 \ub54c, \uae30\ud55c\uc740 \uc815\ud574\uc838 \uc788\uace0 \ud568\uaed8 \ub17c\uc758\ud560 \uc0ac\ub78c\uc740 \uc5c6\uc73c\ub2c8 \uadf8\ub7ec\ud55c \uc555\ubc15\uac10\uc774 \uc8fc\ub294 \uc2a4\ud2b8\ub808\uc2a4\uac00 \uc788\uc5c8\ub2e4.\\n\\n\uc5b4\ub5a4 \ud0dc\uc2a4\ud06c\ub97c \uc9c4\ud589\ud558\uace0 \uc788\ub294\ub370 \uc0c8\ub86d\uac8c \ub2e4\ub978 \ud0dc\uc2a4\ud06c\uac00 \ub4e4\uc5b4\uc624\ub294 \uac83\uc758 \ud578\ub4e4\ub9c1\uc774 \ud798\ub4e4\uc5c8\ub2e4. \uc6d0\ub798\ub77c\uba74 \ub2e4\ub978 \ub3d9\ub8cc\uc640 \uc798 \ubd84\ubc30\ud574\uc11c \ucc98\ub9ac\ud588\uc744 \uc77c\uc778\ub370 \uc774\uc81c\ub294 \uc5b4\ub5bb\uac8c\ub4e0 \ud63c\uc790 \ud574\ub0b4\uc57c \ud558\ub294 \uc77c\uc774 \ub410\ub2e4.\\n\\n\uc77c\ub2e8 \uc77c\uc774 \uc0c8\ub86d\uac8c \ub4e4\uc5b4\uc624\uba74 \uc6b0\uc120\uc21c\uc704\uc640 \ub9c8\uac10 \uae30\ud55c\uc5d0 \ub300\ud574\uc11c \ucee4\ubba4\ub2c8\ucf00\uc774\uc158\uc744 \ud588\ub2e4. \uae09\ud55c \uc77c\uc774\ub77c\uba74 \uc9c4\ud589 \uc911\uc778 \uc77c\uc774 \uc788\ub354\ub77c\ub3c4 \ubbf8\ub904\ub450\uace0 \uba3c\uc800 \ucc98\ub9ac\ud558\ub294 \ub4f1\uc758 \ud0dc\uc2a4\ud06c \uad00\ub9ac\uac00 \ud544\uc694\ud588\ub2e4. \ud68c\uc0ac Jira\uc640\ub294 \ubcc4\uac1c\ub85c \uac1c\uc778\uc801\uc73c\ub85c ToDoist \ub4f1 \ud234\uc744 \uc0ac\uc6a9\ud574 \uc77c\uac10\uc744 \uc815\ub9ac\ud588\ub2e4. \uac1c\uc778 \ud234\uc5d0 \ub4e4\uc5b4\uc628 \uc77c\uc758 \uc6b0\uc120\uc21c\uc704, \ub9c8\uac10\uae30\ud55c, \uc5c5\ubb34 \ub0b4\uc6a9\uacfc \uba54\ubaa8 \ub4f1\uc744 \uc815\ub9ac\ud574 \uc5b4\uca54 \uc218 \uc5c6\uc774 \ub9ce\uc544\uc9c0\ub294 \uc5c5\ubb34 \uac04 context switching\uc744 \ud6a8\uc728\uc801\uc73c\ub85c \ud560 \uc218 \uc788\ub3c4\ub85d \ub178\ub825\ud588\ub2e4.\\n\\n\ub3cc\uc774\ucf1c\ubcf4\uba74 \uc77c\uc774 \ub9ce\uc558\ub358 \uac83\ub3c4 \uc788\uc5c8\uaca0\uc9c0\ub9cc \ud63c\uc790\ub77c\ub294 \uc0ac\uc2e4\uc774 \uc8fc\ub294 \uc2ec\ub9ac\uc801 \uc555\ubc15\uac10\uc5d0 \ub354\uc6b1 \uc774\uac83\uc800\uac83 \ub3c4\uad6c\ub098 \ubc29\ubc95 \ub4f1\uc744 \ucc3e\uc558\ub358 \uac83 \uac19\ub2e4.\\n\\n\uacb0\uacfc\uc801\uc73c\ub85c \ud070 \uc774\uc288 \uc5c6\uc774 \ud640\ub85c \ud68c\uc0ac\uc758 \uc6f9\uc744 \uc57d 4\uac1c\uc6d4\uac04 \uc6b4\uc601\ud588\ub2e4. \uc2e0\uaddc \uc785\uc0ac\uc790 \ubd84\uc774 \uc624\uc154\uc11c 1\uc778\ud300 \uc0dd\ud65c\uc740 \ub9c8\ubb34\ub9ac\ud560 \uc218 \uc788\uc5c8\ub2e4. \uc6f9 \uac1c\ubc1c\uc758 \ubcd1\ubaa9\uc774 \uc81c\ud488 \uac1c\ubc1c \uc804\uccb4\uc758 \ubcd1\ubaa9\uc774 \ub418\ub294 \uc0c1\ud669\uc774 \ub450\ub824\uc6e0\ub294\ub370 \uadf8\ub7f0 \uc0c1\ud669\uc740 \uc77c\uc5b4\ub098\uc9c0 \uc54a\uc544\uc11c \ub2e4\ud589\uc774\ub2e4. \ub610\ud55c \ub0b4 \uc758\uc0ac\uacb0\uc815\uc774 \ubbf8\uce58\ub294 \ubc94\uc704\uac00 \uc0c1\ub300\uc801\uc73c\ub85c \ucee4\uc9c0\ub294 \uc0c1\ud669\uc744 \uacbd\ud5d8\ud558\uace0 \uc775\uc219\ud574\uc9c8 \uc218 \uc788\uc5c8\ub358 \uac83 \uac19\ub2e4.\\n\\n---\\n\\n- \ucc44\uc6a9 \ud504\ub85c\uc138\uc2a4 \ucc38\uc5ec\\n\\n\uac19\uc774 \uc77c\ud560 \ud300\uc6d0\uc744 \ubf51\ub294 \ucc44\uc6a9 \ud504\ub85c\uc138\uc2a4\uc5d0\ub3c4 \ucc38\uc5ec\ud588\ub2e4. \uba74\uc811\uad00\uc73c\ub85c\uc11c \uba74\uc811\uc5d0 \ucc38\uc5ec\ud558\ub294 \uc77c\uc774 \ucc98\uc74c\uc5d0\ub294 \uae34\uc7a5\ub418\uace0 \ubd80\ub2f4\uc2a4\ub7ec\uc6e0\ub2e4. \ud68c\uc0ac\uc758 \uc5bc\uad74\ub85c\uc11c \uacf5\uc801\uc778 \uc790\ub9ac\uc5d0 \ucc38\uc5ec\ud55c\ub2e4\uace0 \uc0dd\uac01\ud558\ub2c8 \uc5b4\uae68\uac00 \ubb34\uac81\uac8c \ub290\uaef4\uc84c\ub2e4.\\n\\n\uc790\uc5f0\uc2a4\ub808 \uc9c0\uc6d0\uc790\ub97c \uc5b4\ub5bb\uac8c \ud3c9\uac00\ud560 \uac83\uc778\uac00? \ud639\uc740 \uc5b4\ub5a4 \uc9c8\ubb38\uc744 \ud560 \uac83\uc778\uac00? \uc5d0 \ub300\ud55c \uace0\ubbfc\ub3c4 \uc774\uc5b4\uc84c\ub2e4.\\n\\n\uac1c\uc778\uc801\uc73c\ub85c \ud034\uc988 \uc2dd\uc758 \uba74\uc811\uc740 \uba74\uc811\uc790\ub97c \uac80\uc99d\ud558\ub294\ub370 \ud070 \ub3c4\uc6c0\uc774 \ub418\uc9c0 \uc54a\ub294\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uc65c\ub0d0\ud558\uba74 \uadf8\ub7f0 \uc2dd\uc758 \uc9c8\ubb38\ub4e4\uc740 **\uba74\uc811 \uc900\ube44\ub97c \ud588\ub2e4\uba74** \ub300\ub2f5\uc774 \uac00\ub2a5\ud55c \uc885\ub958\uc758 \uc9c8\ubb38\uc774\uae30 \ub54c\ubb38\uc774\ub2e4. \ud639\uc740 \uc5b4\ub5a4 \uc9c8\ubb38\uc740 \uc2e4\ubb34\uc640 \ub9e4\uc6b0 \ub3d9\ub5a8\uc5b4\uc838 \uc900\ube44\ub97c \'\ud574\uc57c\ub9cc\' \ub300\ub2f5\uc774 \uac00\ub2a5\ud55c \uacbd\uc6b0\ub3c4 \uc788\uc744 \uac83\uc774\ub2e4.\\n\\n\ub0b4\uac00 \uac19\uc774 \uc77c\ud558\uace0 \uc2f6\uc740 \uc0ac\ub78c\uc740 \uba74\uc811 \uc900\ube44\ub97c \uc798 \ud558\ub294 \uc0ac\ub78c\uc740 \uc544\ub2c8\uc5c8\ub2e4. \ubcbc\ub77d \uce58\uae30\uac00 \uac00\ub2a5\ud55c \uc9c8\ubb38\uc5d0 \ub300\ub2f5\uc744 \uc798\ud55c\ub2e4\uace0 \ud574\uc11c \uc870\uc9c1\uc5d0\uc11c \uc6d0\ud558\ub294 \uc5ed\ub7c9\uc744 \uac80\uc99d\ud560 \uc218 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ud588\ub2e4.\\n\\n\ud034\uc988 \uac19\uc740 \uc9c8\ubb38\uc744 \ucd5c\ub300\ud55c \uc9c0\uc591\ud558\uace0 \uba74\uc811\uc790 \ubd84\uc774 \ubb38\uc81c\ub97c \uc811\uadfc\ud558\ub294 \ubc29\ubc95\uc774\ub098 \uc5b4\ub5a4 \uc8fc\uc81c\uc5d0 \ub300\ud55c \uac1c\uc778\uc801\uc778 \uc758\uacac\ub4e4\uc5d0 \ub300\ud574\uc11c \uc9c8\ubb38\ud588\ub2e4. \ub610\ud55c \uadf8\uac83\uc744 \ud45c\ud604\ud558\ub294 \ucee4\ubba4\ub2c8\ucf00\uc774\uc158 \ub0b4\uc9c0\ub294 \ud0dc\ub3c4 \ub610\ud55c \uc9d1\uc911\ud574\uc11c \ubcf4\uc558\ub2e4.\\n\\n---\\n\\n- \uc815\ub2f5 \ub9de\ucd94\uae30\\n\\n\ucf54\ub4dc\uc5d0 \uc815\ub2f5\uc774 \uc788\ub2e4\uace0 \uc0dd\uac01\ud558\uace4 \ud588\ub2e4. \uc791\uc5c5\uc744 \uc2dc\uc791\ud558\uae30 \uc804\uc5d0 best practice \ub530\uc704\uc758 \uac80\uc0c9 \uacb0\uacfc\ub97c \uc8fc\uc6b1 \ud6d1\uc5c8\ub2e4. \ubb38\uc81c\uc5d0 \ub300\ud574 \ub0b4\uac00 \ub5a0\uc62c\ub9ac\uc9c0 \ubabb\ud558\uace0 \uc788\ub294 \uc815\ub2f5\uc774 \uc788\uc744 \uac70\ub77c\uace0 \uc0dd\uac01\ud588\ub2e4.\\n\\n\uc9c0\uae08\uc73c\ub85c\uc11c \ub0b4\ub9b0 \uacb0\ub860\uc740 \uc124\uc0ac \uadf8\uac83\uc774 \uc874\uc7ac\ud558\ub354\ub77c\ub3c4 \uadf8\ub2e4\uc9c0 \uc911\uc694\ud558\uc9c0 \uc54a\ub2e4\ub294 \uac83\uc774\ub2e4. \uc694\uad6c\uc0ac\ud56d\uc740 \ud56d\uc0c1 \ubcc0\ud654\ud558\uace0 \uadf8\ub85c\uc778\ud574 \'\uadf8\ub54c\ub294 \ub9de\uc9c0\ub9cc \uc9c0\uae08\uc740 \ud2c0\ub9ac\ub2e4\'\uc640 \uac19\uc740 \uc0c1\ud669\uc774 \uacc4\uc18d \ubc1c\uc0dd\ud55c\ub2e4. \uadf8 \ub54c \uadf8\ub807\uac8c \ud0d0\ub2c9\ud588\ub358 \uc815\ub2f5\uc740 \uc9c0\uae08\uc5d0 \uc640\uc11c\ub294 \uc758\ubbf8\uc5c6\ub294 \uac83\uc774 \ub418\ub294 \uacbd\uc6b0\uac00 \ub9ce\uc558\ub2e4. \uacb0\ub860\uc801\uc73c\ub85c\ub294 \uc815\ub2f5\uc5d0 \ub300\ud55c \ud0d0\ub2c9\uc740 \uadf8\ub9cc\ub450\uae30\ub85c \ud588\ub2e4. \uc774\ub294 \ucf54\ub4dc \ud004\ub9ac\ud2f0\ub97c \ud3ec\uae30\ud558\uaca0\ub2e4\ub294 \uc758\ubbf8\ub294 \uc804\ud600 \uc544\ub2c8\ub2e4. \ubb38\uc81c\ub97c \ubc14\ub77c\ubcf4\ub294 \uad00\uc810\uc774 \ub2ec\ub77c\uc84c\ub2e4\ub294 \uc815\ub3c4\uc758 \uc758\ubbf8\ub85c \ubcfc \uc218 \uc788\uaca0\ub2e4. \ubcc0\uacbd\uc744 \uac10\ud788 \uc608\uce21\ud560 \uc218\ub294 \uc5c6\uc9c0\ub9cc, \ubcc0\uacbd\uc774 \uc6a9\uc774\ud55c \ucf54\ub4dc\ub97c \ub9cc\ub4e4\uc5b4\uc57c \ud55c\ub2e4\ub294 \uc0dd\uac01\uc774\ub2e4.\\n\\n\uc9c0\uae08\uae4c\uc9c0 \uac19\uc774 \uc77c\ud558\uba70 \'\uc774\ub7f0 \uc0ac\ub78c\uacfc \uc77c\ud558\uba74 \uc88b\uc558\ub2e4\'\ub77c\ub294 \uc0ac\ub78c\ub4e4\uc758 \ud2b9\uc9d5\uc744 \ub5a0\uc62c\ub824 \ubcf4\uc558\ub2e4. \uadf8\ub7f0 \uc0ac\ub78c\ub4e4\uc740 \ud56d\uc0c1 \uc815\ub2f5\uc744 \uc81c\uc2dc\ud558\ub294 \uc0ac\ub78c\ub4e4\uc740 \uc544\ub2c8\uc5c8\ub2e4. \uc5ec\ub7ec \ubaa8\uc2b5\ub4e4\uc774 \uc788\uaca0\uc9c0\ub9cc \uadf8\ub4e4\uc740 \ub300\uccb4\ub85c \uc8fc\uc5b4\uc9c4 \uae30\ud55c\uc744 \uc900\uc218\ud558\uace0, \uacfc\uc815\uacfc \uacb0\uacfc\ub97c \uacf5\uc720\ud558\uace0, \uafb8\uc900\ud788 \uac1c\uc120\uc744 \ud574\ub0b4\ub294 \uc0ac\ub78c\ub4e4\uc774\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4. \ud3b8\uc548\ud55c \ub17c\uc758 \ud30c\ud2b8\ub108\uc774\uae30\ub3c4 \ud588\ub2e4. \uadf8\ub807\ub2e4\uba74 \ub098 \ub610\ud55c \ud56d\uc0c1 \ud034\uc988\uc758 \uc815\ub2f5\uc744 \ub9de\ucd94\ub294 \uc0ac\ub78c\uc77c \ud544\uc694\ub294 \uc5c6\uc744 \uac83\uc774\ub2e4."},{"id":"good-bye-saga","metadata":{"permalink":"/blog/good-bye-saga","source":"@site/blog/2024-02-12-good-bye-saga.md","title":"redux-saga\uc640 \ud5e4\uc5b4\uc9c8 \uacb0\uc2ec","description":"redux-saga\ub294 redux \ubbf8\ub4e4\uc6e8\uc5b4\ub85c, \ub9e4\uc6b0 \uac15\ub825\ud55c \uc0ac\uc774\ub4dc \uc774\ud399\ud2b8 \ucc98\ub9ac\ub97c \uac00\ub2a5\ud558\uac8c \ub9cc\ub4dc\ub294 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub2e4.","date":"2024-02-12T00:00:00.000Z","formattedDate":"2024\ub144 2\uc6d4 12\uc77c","tags":[{"label":"redux","permalink":"/blog/tags/redux"},{"label":"redux-saga","permalink":"/blog/tags/redux-saga"},{"label":"server state","permalink":"/blog/tags/server-state"}],"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"good-bye-saga","title":"redux-saga\uc640 \ud5e4\uc5b4\uc9c8 \uacb0\uc2ec","tags":["redux","redux-saga","server state"]},"unlisted":false,"prevItem":{"title":"\ub0b4\uac00 2023\ub144\uc5d0 \ubc30\uc6b4 \uac83\ub4e4","permalink":"/blog/2023-retro"},"nextItem":{"title":"ts-pattern\uc744 \ub3c4\uc785\ud574\ub3c4 \ub420\uae4c?","permalink":"/blog/can-i-use-ts-pattern"}},"content":"`redux-saga`\ub294 redux \ubbf8\ub4e4\uc6e8\uc5b4\ub85c, \ub9e4\uc6b0 \uac15\ub825\ud55c \uc0ac\uc774\ub4dc \uc774\ud399\ud2b8 \ucc98\ub9ac\ub97c \uac00\ub2a5\ud558\uac8c \ub9cc\ub4dc\ub294 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub2e4.\\n\\n\ucd5c\uadfc \ud68c\uc0ac \ucf54\ub4dc\ubca0\uc774\uc2a4\uc5d0\uc11c \uc774 \uc758\uc874\uc131\uc744 \uc81c\uac70\ud558\uace0\uc790 \ud558\uc5ec \uadf8 \uc758\uc0ac\uacb0\uc815\uc758 \uadfc\uac70\ub4e4\uc744 \ub0a8\uae34\ub2e4.\\n\\n> \uc774 \uae00\uc740 \uc77d\ub294 \uc0ac\ub78c\uc774 `redux-saga`\uc640 `redux-toolkit`\uc744 \uc774\uc6a9\ud55c \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c \uacbd\ud5d8\uc774 \uc788\ub2e4\uace0 \uac00\uc815\ud55c\ub2e4.\\n\\n---\\n\\n### 1. \ub7ec\ub2dd\ucee4\ube0c\uc758 \uad00\uc810\\n\\nGenerator\ub294 saga \uc774\uc678\uc758 \uc0ac\uc6a9\ucc98\ub97c \ucc3e\uc544\ubcf4\uae30 \ud798\ub4e4\ub2e4. \ub300\ubd80\ubd84\uc758 \uc785\ubb38\uc790\ub294 saga\ub97c \ubc30\uc6b0\ub824\uba74 \uba3c\uc800 generator\ubd80\ud130 \uacf5\ubd80\ud574\uc57c \ud55c\ub2e4.\\n\\n\ub610\ud55c saga\uac00 \uc81c\uc2dc\ud558\ub294 \uba58\ud0c8 \ubaa8\ub378\uc5d0 \uc775\uc219\ud574\uc838\uc57c \ud55c\ub2e4. [\uacf5\uc2dd \ubb38\uc11c](https://redux-saga.js.org/docs/About/)\uc5d0 \ub530\ub974\uba74,\\n\\n> The mental model is that a saga is like a separate thread in your application that\'s solely responsible for side effects.\\n\\n\uc0ac\uac00\uc758 \uba58\ud0c8 \ubaa8\ub378\uc740 \uc5b4\ud50c\ub9ac\ucf00\uc774\uc158\uc5d0\uc11c \uc0ac\uc774\ub4dc \uc774\ud399\ud2b8\ub97c \ucc45\uc784\uc9c0\ub294 \ubcc4\ub3c4\uc758 \uc2a4\ub808\ub4dc\uc640 \uac19\ub2e4. \uc77c\ubc18\uc801\uc778 React \uac1c\ubc1c \uacbd\ud5d8\uacfc\ub294 \uc0c1\ub2f9\ud788 \uc774\uc9c8\uc801\uc774\uc5b4\uc11c \uc775\uc219\ud574\uc9c0\ub294\ub370 \uc2dc\uac04\uc774 \ud544\uc694\ud558\ub2e4.\\n\\n---\\n\\n### 2. \ucf54\ub4dc \ubcc0\uacbd\uc73c\ub85c \uc778\ud55c \uc0ac\uc774\ub4dc \uc774\ud399\ud2b8 \ud1b5\uc81c\uc758 \uad00\uc810\\n\\n\uc0ac\uac00\ub294 `generator function`\uacfc (\uc774\ud558 `saga function`) \uc774\ub4e4\uc744 \ud2b8\ub9ac\uac70 \uc2dc\ud0a4\ub294 action\uc758 `watcher`\uac00 \ud568\uaed8 \uc4f0\uc774\ub294 \uac83\uc774 \uc77c\ubc18\uc801\uc778 \ud328\ud134\uc774\ub2e4.\\n\\n\uc774\ub7ec\ud55c \ud328\ud134\uc73c\ub85c \uc778\ud574 `saga function`\uc744 \uc2e4\ud589\ud558\ub294\ub370\ub294 \ud06c\uac8c \uc138 \uac00\uc9c0\uc758 \ubc29\ubc95\uc774 \uc788\ub2e4.\\n\\n1. `call`\\n2. `fork`\\n3. `put` / `dispatch`\\n\\n\uc774 \uc138 \ubc29\uc2dd\uc740 \ud50c\ub85c\uc6b0 \ucee8\ud2b8\ub864\uc5d0 \uc788\uc5b4 \ub2e4\ub978 \ud2b9\uc9d5\uc744 \uac00\uc9c0\ubbc0\ub85c, \uc5b4\ub5a4 \uad6c\ud604\uc744 \uc6d0\ud558\ub290\ub0d0\uc5d0 \ub530\ub77c \uc801\uc808\ud788 \uc120\ud0dd\ud574\uc57c \ud55c\ub2e4.\\n\\n\ubb38\uc81c\ub294 \ud2b9\uc815 `saga function`\uc758 body\ub97c \ubcc0\uacbd\ud558\ub824\uba74, \ud574\ub2f9 `saga function`\uc774 \uc138 \uac00\uc9c0 \uc911 \uc5b4\ub5a4 \ubc29\uc2dd\uc73c\ub85c \ud638\ucd9c\ub418\uace0 \uc788\ub290\ub0d0\ub97c \uc0b4\ud3b4\ubd10\uc57c \ud55c\ub2e4\ub294 \uc810\uc774\ub2e4. \uc989 \ud638\ucd9c\ubd80\ub97c \uba74\ubc00\ud788 \uc0b4\ud3b4\ubd10\uc57c \uc120\uc5b8\ubd80\ub97c \uace0\uce58\uae30 \uc704\ud55c \ud655\uc2e0\uc744 \uc5bb\uc744 \uc218 \uc788\uc5c8\ub2e4.\\n\\n\uc774\ub294 \uacb0\uad6d \ucf54\ub4dc\ub97c \uc801\uadf9\uc801\uc73c\ub85c \ubcc0\uacbd\ud558\uae30 \ud798\ub4e4\uac8c \ub9cc\ub4e0\ub2e4. \ucf54\ub4dc\uc758 \ubcc0\uacbd\uc73c\ub85c \uc778\ud55c \uc0ac\uc774\ub4dc \uc774\ud399\ud2b8 \ud1b5\uc81c\uac00 \uc5b4\ub835\ub2e4\uace0\ub3c4 \ud45c\ud604\ud560 \uc218 \uc788\ub2e4.\\n\\n---\\n\\n### 3. \uc7ac\uc0ac\uc6a9\uc758 \uc5b4\ub824\uc6c0 \uad00\uc810\\n\\n`saga function`\uc744 \uc7ac\uc0ac\uc6a9\ud558\uae30 \uc704\ud574\uc11c\ub294 \ub85c\uc9c1\uc744 \ucd94\ucd9c\ud558\uace0 \uadf8 \ub85c\uc9c1\uc5d0 \ub300\ud55c \ud50c\ub85c\uc6b0 \ucee8\ud2b8\ub864\uc774 \ud544\uc694\ud558\ub2e4. \uc5ec\uae30\uc11c \ud50c\ub85c\uc6b0 \ucee8\ud2b8\ub864\uc5d0\ub294 \uc790\uc5f0\uc2a4\ub7fd\uac8c \uadf8 \ub85c\uc9c1\uc5d0 \ub300\ud55c \ud638\ucd9c\uc774 \ud3ec\ud568\ub41c\ub2e4.\\n\\n\uc608\ub97c \ub4e4\uc5b4 `saga function` \ub0b4\uc5d0\uc11c \ub2e4\ub978 `saga function`\uc744 \ud638\ucd9c\ud55c\ub2e4\uba74 2\uc5d0\uc11c \uc5b8\uae09\ud588\ub358 \uc138 \uac00\uc9c0 \ubc29\uc2dd \uc911 \ud558\ub098\ub85c \ud638\ucd9c\ud558\uac8c \ub41c\ub2e4. \uc989 \uc7ac\uc0ac\uc6a9\uc744 \ud558\uae30 \uc704\ud574\uc11c 2\uc5d0\uc11c \ub9d0\ud55c\ub300\ub85c **\uc801\uadf9\uc801\uc73c\ub85c \ubcc0\uacbd\ud558\uae30 \uc5b4\ub824\uc6b4 \ucf54\ub4dc\ub97c \uc791\uc131**\ud558\uac8c \ub418\ub294 \uac83\uc774\ub2e4.\\n\\n\ub610\ud55c saga\ub97c \uc0c8\ub85c \ub9cc\ub4dc\ub824\uba74 slice\uc5d0 \uc774\ub97c \uc704\ud55c action\ub4e4\uc744 \ucd94\uac00\ub85c \uc120\uc5b8\ud574\uc8fc\uc5b4\uc57c \ud55c\ub2e4.\\n\uc774\ub294 \uc5b4\ub290\uc815\ub3c4\ub294 \ud544\uc5f0\uc801\uc73c\ub85c \uc5b4\ub824\uc6b4 \uac83\uc73c\ub85c \ubcf4\uc558\ub2e4. \uc65c\ub0d0\ud558\uba74 slice\ub294 \uae30\ubcf8\uc801\uc73c\ub85c **\ub370\uc774\ud130**\ub97c \ub2f4\uae30 \uc704\ud55c \ub2e8\uc704\uc774\ub2e4. \uc5ec\uae30\uc5d0 **\ub85c\uc9c1**\uc758 \uc7ac\uc0ac\uc6a9\uc744 \uc704\ud55c saga\ub97c \uc704\ud55c action\uc744 \ub2f4\ub294\ub2e4\ub294 \uac83\uc740 \ubcf8\uc9c8\uc801\uc778 \ubd88\uc77c\uce58\uac00 \uc874\uc7ac\ud55c\ub2e4.\\n\\n\uacb0\uad6d \ud574\ub2f9 saga\uc758 action\ub4e4\uc744 \ub123\uc744 \uc801\uc808\ud55c slice\uac00 \uc5c6\ub294 \uc0c1\ud669\uc774 \uc885\uc885 \ubc1c\uc0dd\ud55c\ub2e4. \uc774\ub7f0 \uc0c1\ud669 \ub54c\ubb38\uc5d0 \uc6b0\ub9ac\ub294 \uc885\uc885 \uc5b4\uca54 \uc218 \uc5c6\uc774 saga\uc758 action\ub9cc\uc744 \ub2f4\uace0\uc788\ub294 slice\ub97c \ub9cc\ub4e4\uace4 \ud588\ub2e4.\\n\\n---\\n\\n### 4. redux store\uac00 \ube44\ub300\ud574\uc9c0\ub294 \ubb38\uc81c\\n\\nsaga\ub294 redux store\ub97c \ube44\ub300\ud558\uac8c \ub9cc\ub4e0\ub2e4. \uc5ec\uae30\uc11c \ube44\ub300\ud558\uac8c \ub9cc\ub4e0\ub2e4\ub294 \ud45c\ud604\uc758 \uc815\ud655\ud55c \ub73b\uc740, \ubcf8\ub798 \ud544\uc694\ud55c \uc815\ub3c4\ubcf4\ub2e4 \ube44\ub300\ud574 \uc9c4\ub2e4\ub294 \ub73b\uc774\ub2e4.\\n\\nsaga\ub294 \ube44\ub3d9\uae30 \ub85c\uc9c1\uc744 \ucc98\ub9ac\ud55c \ub4a4 redux store\uc5d0 \ub2f4\uae30 \uc704\ud55c \uc758\uc874\uc131\uc774\ub2e4. \uc989, store\uc5d0 \ub2f4\uae30\ub294 \ud574\ub2f9 \ub370\uc774\ud130 \uc678\uc758 \ucd94\uac00\uc801\uc73c\ub85c redux store\ub97c \ube44\ub300\ud558\uac8c \ub9cc\ub4dc\ub294 \uac83\uc740 saga\uac00 \ud574\uacb0\ud558\ub294 \ubb38\uc81c\uc640\ub294 \uad00\ub828 \uc5c6\ub294 \ubd88\ud544\uc694\ud55c \uc624\ubc84\ud5e4\ub4dc\ub77c\uace0 \uc0dd\uac01\ud588\ub2e4.\\n\\nredux store\uac00 \ube44\ub300\ud574\uc9c0\uba74 \uc804\ubc18\uc801\uc778 \ucf54\ub4dc\ubca0\uc774\uc2a4 \ud30c\uc545\uc774 \ud798\ub4e4\uc5b4\uc9c0\uace0, \ubc88\ub4e4\ub3c4 \ubd88\ud544\uc694\ud558\uac8c \ucee4\uc9c4\ub2e4.\\n\\n\ub610\ud55c \uc6b0\ub9ac \ucf54\ub4dc\ub294 side effect \uc758 \uc81c\uc5b4\ub97c \uc81c\uc678\ud55c \ubd80\ubd84\uae4c\uc9c0\ub3c4 saga\ub85c \uad6c\ud604\ub41c \ubd80\ubd84\uc774 \ub2e4\uc218 \uc874\uc7ac\ud588\ub2e4. \uc608\ub97c \ub4e4\uba74 \ud31d\uc5c5\uc744 \uc5f4\uac70\ub098 \ub2eb\ub294 \ub85c\uc9c1\uc870\ucc28 saga\ub85c \uc791\uc131\ub418\uc5b4 \uc788\ub2e4. \uc5b4\ub5bb\uac8c \ubcf4\uba74 \ube44\uc988\ub2c8\uc2a4 \ub85c\uc9c1\uae4c\uc9c0 saga\ub77c\ub294 \ub808\uc774\uc5b4\uc5d0 \ud3ec\ud568\uc2dc\ud0a8 \uac83\uc774\ub2e4. \uadf8 \uacb0\uacfc saga\uc5d0\uc11c select \ud639\uc740 put\ud558\uae30 \uc704\ud574 redux\uc5d0 \uc788\uc744 \ud544\uc694 \uc5c6\ub294 \uc0c1\ud0dc \uc870\ucc28 redux\uc5d0 \ub123\uace0 \uc788\uc5c8\ub2e4.\\n\uc774\ub294 \ub610 \ud55c \ubc88 store\ub97c \ube44\ub300\ud558\uac8c \ub9cc\ub4e4\uc5c8\ub2e4.\\n\\n---\\n\\n### 5. Typescript \ud0c0\uc774\ud551\uc758 \uad00\uc810\\n\\nsaga\ub294 Typescript native\ub85c \uc791\uc131\ub41c \ub77c\uc774\ube0c\ub7ec\ub9ac\uac00 \uc544\ub2c8\ub2e4. \ud0c0\uc774\ud551\uc744 \uac1c\uc120\ud558\uae30 \uc704\ud55c \ucee4\ubba4\ub2c8\ud2f0 \ub77c\uc774\ube0c\ub7ec\ub9ac (i.e. `typed-redux-saga`) \ub4e4\uc774 \uc788\uc9c0\ub9cc \uc774\uac83\ub3c4 \uadf8\ub9ac \ub9cc\uc871\uc2a4\ub7ec\uc6b4 \ud0c0\uc785\uc2a4\ud06c\ub9bd\ud2b8 \ucf54\ub529 \uacbd\ud5d8\uc744 \uc8fc\uc9c4 \ubabb\ud588\ub2e4.\\n\\n\ub2e4\ub9cc \uac1c\uc778\uc801\uc73c\ub85c DX\ub294 \uacb0\uad6d \ube44\uc988\ub2c8\uc2a4\ub97c \uc9c0\ud0f1\ud558\uae30 \uc704\ud55c \ucd5c\uc18c \uc0dd\uc0b0\uc131\uc744 \ub2f4\ubcf4\ud558\uae30 \uc704\ud55c \uc120\ub9cc \uc9c0\ucf1c\ub3c4 \ub41c\ub2e4\uace0 \uc0dd\uac01\ud558\uace0, `typed-redux-saga`\ub97c \uc774\uc6a9\ud55c \uac1c\ubc1c \uacbd\ud5d8\uc774 \uc544\uc2ac\uc544\uc2ac\ud558\uc9c0\ub9cc \uadf8 \uc120 \uc774\uc0c1\uc740 \uc9c0\ucf1c\uc900\ub2e4\uace0 \uc0dd\uac01\ud558\uae30\uc5d0 \uc5b8\uae09\ub9cc \ud558\uace0 \ub118\uc5b4\uac00\uaca0\ub2e4.\\n\\n---\\n\\n\uc704\uc640 \uac19\uc740 \uc774\uc720\ub4e4\ub85c saga\ub97c \ub5a0\ub098 \ub2e4\ub978 server state \uad00\ub9ac \ub77c\uc774\ube0c\ub7ec\ub9ac\ub85c \ub9c8\uc774\uadf8\ub808\uc774\uc158\ud558\ub294 \uac83\uc73c\ub85c \uacb0\uc815\ud588\ub2e4. \uadf8\ub9ac\uace0 \uc6b0\ub9ac\uc758 \ub2e4\uc74c \uc2a4\ud15d\uc740 `rtk-query`\ub85c \uacb0\uc815\ud588\ub2e4. \uad00\ub828\ub418\uc5b4 \uc5b4\ub5a4 \uc5b4\ub824\uc6c0\uc774 \uc788\uc5c8\uace0, \uc88b\uc558\ub358 \uc810\uc740 \ubb34\uc5c7\uc774 \uc788\ub294\uc9c0 \ub4f1 \uad00\ub828\ub41c \uc774\uc57c\uae30\ub294 \ub2e4\uc74c \uae00\uc5d0\uc11c \ub2e4\ub904 \ubd10\uc57c\uaca0\ub2e4.\\n\\n\uc9c0\uae08\uae4c\uc9c0 \uc6b0\ub9ac\uc758 \uc6f9 \uac1c\ubc1c \uc2a4\ud0dd\uc744 \uc798 \uc9c0\ud0f1\ud574\uc900 saga\uc5d0\uac8c \uac10\uc0ac\ub97c \ud45c\ud558\uba70..."},{"id":"can-i-use-ts-pattern","metadata":{"permalink":"/blog/can-i-use-ts-pattern","source":"@site/blog/2023-12-09-can-i-use-ts-pattern.md","title":"ts-pattern\uc744 \ub3c4\uc785\ud574\ub3c4 \ub420\uae4c?","description":"ts-pattern\uc740 \ud328\ud134 \ub9e4\uce6d\uc744 Typeacript \ud658\uacbd\uc5d0\uc11c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub2e4.","date":"2023-12-09T00:00:00.000Z","formattedDate":"2023\ub144 12\uc6d4 9\uc77c","tags":[{"label":"typescript","permalink":"/blog/tags/typescript"}],"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"can-i-use-ts-pattern","title":"ts-pattern\uc744 \ub3c4\uc785\ud574\ub3c4 \ub420\uae4c?","tags":["typescript"]},"unlisted":false,"prevItem":{"title":"redux-saga\uc640 \ud5e4\uc5b4\uc9c8 \uacb0\uc2ec","permalink":"/blog/good-bye-saga"},"nextItem":{"title":"useEffect cleanup\uacfc \ud37c\ub110 \uad00\ub9ac\uc5d0 \ub300\ud55c \uc0dd\uac01\ub4e4","permalink":"/blog/use-effect-with-funnel"}},"content":"[ts-pattern](https://github.com/gvergnaud/ts-pattern)\uc740 \ud328\ud134 \ub9e4\uce6d\uc744 Typeacript \ud658\uacbd\uc5d0\uc11c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub2e4.\\n[\ud328\ud134 \ub9e4\uce6d](https://en.wikipedia.org/wiki/Pattern_matching)\uc740 \uc8fc\ub85c \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub798\ubc0d \uc5b8\uc5b4\uc5d0\uc11c \uc870\uac74 \ubc0f \ubd84\uae30 \uad6c\ud604\uc744 \uc704\ud574 \uc0ac\uc6a9\ud55c\ub2e4\uace0 \ud55c\ub2e4.\\n\\nReact \ucef4\ud3ec\ub10c\ud2b8\uc5d0\uc11c \uc0bc\ud56d \uc5f0\uc0b0\uc790 \ub4f1\uc744 \uc0ac\uc6a9\ud574 \uc870\uac74\ubd80 \ub80c\ub354\ub9c1 \uad6c\ud604\uc744 \ud558\ub2e4\uac00 \ub300\uc548\uc774 \uc5c6\uc744\uae4c \uc2f6\uc5b4 \ucc3e\uc544\ubcf4\ub2e4\uac00 \ubc1c\uacac\ud55c \ub77c\uc774\ube0c\ub7ec\ub9ac\ub2e4. Github\uc5d0 \uac78\ub824\uc788\ub294 \uc801\uc6a9 \uc774\uc804\uacfc \uc774\ud6c4\ub97c \ube44\uad50\ud55c \uc608\uc2dc\ub97c \ubcf4\uace0 \ud639\ud574\ubc84\ub838\ub2e4. \ud68c\uc0ac \ud504\ub85c\uc81d\ud2b8\uc5d0 \uc801\uc6a9\ud560 \uc218 \uc788\uc744\uc9c0 \uac80\ud1a0\ud574\ubcf4\uace0 \ub0b4\ub9b0 \uacb0\ub860\uacfc \uadf8 \uadfc\uac70\ub97c \uc815\ub9ac\ud574\ubcf4\uc558\ub2e4.\\n\\n### \uc608\uc2dc \ucf54\ub4dc\\n\\n\uc9c0\ub3c4\uc758 \ub9c8\ucee4\ub97c \uc815\uc758\ud558\uae30 \uc704\ud574 \uc544\ub798\uc640 \uac19\uc774 \ud0c0\uc785\uc774 \uc788\ub2e4\uace0 \ud558\uc790.\\n\\n```ts\\nexport type Path = \\"Start\\" | \\"Middle\\" | \\"End\\";\\n\\nexport type Marker = {\\n  iconType: \\"Place\\" | \\"Path\\"; // \ub9c8\ucee4\uc758 \uc885\ub958\ub294 \ub450 \uac00\uc9c0\ub85c, Place \ub9c8\ucee4\uc758 \uacbd\uc6b0\uc5d0\ub294 pathType\uc744 \uac00\uc9c0\uc9c0 \uc54a\ub294\ub2e4.\\n  pathType?: Path;\\n};\\n```\\n\\nts-pattern\uc744 \uc801\uc6a9\ud558\uae30 \uc774\uc804\uc758 \ucf54\ub4dc\ub294 \uc544\ub798\uc640 \uac19\ub2e4.\\n\\n```tsx\\nconst getPathType = (marker: Marker): Path | undefined => {\\n  if (marker.iconType === \\"Path\\") {\\n    if (marker.pathType === \\"Start\\") {\\n      return \\"Start\\";\\n    }\\n    if (marker.pathType === \\"Middle\\") {\\n      return \\"Middle\\";\\n    }\\n    if (marker.pathType === \\"End\\") {\\n      return \\"End\\";\\n    }\\n  }\\n  return undefined;\\n};\\n\\nexport const BeforePattern = () => {\\n  const marker: Marker = {\\n    iconType: \\"Path\\",\\n    pathType: \\"Start\\",\\n  };\\n  return <div>{getPathType(marker)}</div>;\\n};\\n```\\n\\n\uc911\ucca9\ub41c if\ubb38\uc744 \ud1b5\ud574 \uad6c\ud604\ud588\ub2e4.\\n\\nts-pattern\uc744 \uc801\uc6a9\ud560 \uc2dc \uc544\ub798\uc640 \uac19\uc740 \uad6c\ud604\uc774 \uac00\ub2a5\ud558\ub2e4.\\n\\n```tsx\\nexport const AfterPattern = () => {\\n  const marker: Marker = {\\n    iconType: \\"Pathway\\",\\n    pathType: \\"Start\\",\\n  };\\n  return (\\n    <div>\\n      {match(marker)\\n        .with({ iconType: \\"Pathway\\", pathType: \\"Start\\" }, () => \\"Start\\")\\n        .with({ iconType: \\"Pathway\\", pathType: \\"Middle\\" }, () => \\"Middle\\")\\n        .with({ iconType: \\"Pathway\\", pathType: \\"End\\" }, () => \\"End\\")\\n        .otherwise(() => undefined)}\\n    </div>\\n  );\\n};\\n```\\n\\n\uc774\uc678\uc5d0\ub3c4 \ub193\uce58\ub294 \ubd84\uae30 \ucf00\uc774\uc2a4\uac00 \uc5c6\ub3c4\ub85d `exhaustive()` \ub97c \ucd94\uac00\ud558\uac70\ub098, \ubd84\uae30 \ubc0f type narrowing\uc744 \uc704\ud574 `when()`\ub4f1\uc744 \uc720\uc6a9\ud558\uac8c \uc4f8 \uc218 \uc788\uc744 \uac83 \uac19\ub2e4.\\n\\n### \ub0b4 \uc0dd\uac01\\n\\n1.  \uc0c8\ub85c\uc6b4 \uc5b8\uc5b4\ub97c \ubc30\uc6b0\ub294 \uac83 \uac19\uc544 \uc7ac\ubc0c\uc5c8\uace0, \ud655\uc2e4\ud788 \uc77c\ubc18\uc801\uc778 if, switch \ubd84\uae30\ubcf4\ub2e4 \uac15\ub825\ud558\ub2e4\\n2.  \uadf8\ub7ec\ub098 \ucd94\uac00\uc801\uc778 \uc0ac\uc6a9\ubc95\uc744 \ubc30\uc6b8\ub9cc\ud07c \ub9ac\ud134\uc774 \ub69c\ub837\ud558\uc9c0 \ubabb\ud55c \ub4ef\\n\\n- \ucf54\ub4dc\ub9cc \ubcf4\uace0 \ub3d9\uc791\uc744 100% \ud655\uc2e4\ud558\uac8c \uc608\uce21\ud558\uae30 \uc5b4\ub824\uc6e0\ub2e4.\\n- \ubc18\ub300\ub85c \ud45c\ud604\ud558\uba74 \uc5b4\ub5a4 \ub3d9\uc791\uc744 \uc6d0\ud560 \ub54c \ubc14\ub85c \ucf54\ub4dc\ub85c \uc62e\uae30\uae30 \uc27d\uc9c0 \uc54a\uc558\ub2e4.\\n- \uc608\ub97c \ub4e4\uc5b4 \uc5b4\ub5a4 \uad6c\ud604\uc744 \ud2b9\uc815 \uc870\uac74\uc5d0 \uacf5\ud1b5\uc73c\ub85c \uc801\uc6a9\uc2dc\ucf1c\uc57c \ud55c\ub2e4\uba74? \uad49\uc7a5\ud788 \uc77c\uc0c1\uc801\uc778 \uad6c\ud604\uc774\uc9c0\ub9cc `ts-pattern`\uc73c\ub85c \uad6c\ud604\ud558\ub824\ub2c8 \uba87 \uac00\uc9c0 \uc758\ubb38\uc810\uc774 \uc788\uc5c8\uace0, \uc774\ub7f0 \ub2e8\uc21c\ud55c \ucf54\ub4dc\uc870\ucc28 \uc0c8\ub85c \ud559\uc2b5\ud574\uc57c\ud55c\ub2e4\ub294 \uc810\uc740 \ucf54\ub4dc\ubca0\uc774\uc2a4\uc5d0 \uc775\uc219\ud558\uc9c0 \uc54a\uc740 \uac1c\ubc1c\uc790\uc5d0\uac8c\ub294 \ud070 \uc9c4\uc785\uc7a5\ubcbd\uc774 \ub420 \uc218 \uc788\ub2e4\uace0 \uc0dd\uac01\ud588\ub2e4.\\n- \uc870\uac74\uacfc \ubd84\uae30\ub294 \uad6c\ud604\uc5d0\uc11c \ud070 \ube44\uc728\uc744 \ucc28\uc9c0\ud558\ub294\ub370, \uc774\uac78 \ub7ec\ub2dd\ucee4\ube0c\uac00 \uc788\ub294 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \ub3c4\uc785\ud560 \ub9cc\ud07c \ubfb0\uc871\ud558\uac8c \uc88b\uc544\uc9c4\ub2e4\ub294 \ub290\ub08c\uc774 \uc5c6\uc5c8\ub2e4. \ud655\uc2e4\ud55c \uc774\ub4dd\uc774 \uc788\ub2e4\uba74 \ub3c4\uc785\uc744 \uace0\ub824\ud588\uaca0\uc9c0\ub9cc, \uadf8 \uc815\ub3c4\ub294 \uc544\ub2c8\ub77c\uace0 \ud310\ub2e8\ud588\ub2e4.\\n\\n3. \uc2e4\uc81c \ud504\ub85c\ub355\uc158 \ucf54\ub4dc\uc5d0\uc11c \uc801\uc6a9\ud560 \ub9cc\ud55c \uc0ac\ub840\ub97c \ucc3e\uc9c0 \ubabb\ud568\\n\\n- ternary operator\uc758 bracket\uc548\uc5d0\uc11c \ubcc0\uc218 \uc120\uc5b8\uc744 \ud55c\ub2e4\uac70\ub098 \ud558\ub294 \uc77c\uc740 \uad73\uc774 \ud560 \uc77c \uc5c6\ub294 \uac83 \uac19\ub2e4.\\n- `exhaustive()`\ub294 \uc88b\uc740 \uac83 \uac19\uc740\ub370, \ub9b0\ud2b8\ub85c \ub300\uccb4\ud560 \uc218 \uc788\uc744 \uac83 \uac19\uc544 \uadf8 \ucabd\uc744 \ucc3e\uc544\ubcf4\ub294\uac8c \ub098\uc744 \uac83 \uac19\ub2e4.\\n- `when()`\uc758 \ucf5c\ubc31\uc73c\ub85c type predicates (i.e: `(val: any): val is undefined => { ... }`)\uac00 \ud3ec\ud568\ub41c \ud0c0\uc785 \uac00\ub4dc \ud568\uc218\ub97c \ub610 \ub9cc\ub4e4\uc5b4\uc57c \ud55c\ub2e4\ub294\uac8c \uc9c0\ub098\uce5c \uc624\ubc84\ud5e4\ub4dc \uac19\ub2e4. \uadf8\ub7f0 \ud568\uc218\ub97c \uc120\uc5b8\ud574\uc57c \ud55c\ub2e4\uba74 \uc6d0\ub798 \ubc29\uc2dd\uc73c\ub85c\ub3c4 \uc798 \ubd84\uae30\ud560 \uc218 \uc788\uc744 \uac83 \uac19\uc558\ub2e4.\\n- \uac1c\uc778\uc801\uc778 \ucde8\ud5a5\uc73c\ub85c \ud2b9\uc815 \uc870\uac74\uc5d0 \ub300\ud55c handler\ub97c `with()`\uc758 \ub450 \ubc88\uc9f8 \uc778\uc790\uc778 function\uc73c\ub85c \ub118\uaca8\uc57c \ud558\ub294\ub370, \uac12\uc73c\ub85c \ucda9\ubd84\ud560 \ub54c\ub3c4 \ud568\uc218 \ud615\ud0dc\ub85c \ub118\uaca8\uc57c \ud558\ub294 \uc810\uc774 \uac00\ub3c5\uc131\uc774 \uc870\uae08 \ub5a8\uc5b4\uc9c4\ub2e4\uace0 \ub290\uaf08\ub2e4.\\n\\n4. \uc870\uac74\uc774 \ubcf5\uc7a1\ud55c \uacbd\uc6b0\ub294 `switch(true)` \ud328\ud134\uc73c\ub85c \ub300\uccb4 \uac00\ub2a5\ud560 \ub4ef\\n\\n- Typescript 5.3 \uc774\uc804\uc5d0\ub294 `switch(true)` \uc5d0\uc11c \uc870\uac74 \ub0b4\uc758 type narrowing\uc774 \ub418\uc9c0 \uc54a\ub294 \ubb38\uc81c\uac00 \uc788\uc5b4\uc11c \uc5b4\ub824\uc6c0\uc774 \uc788\uc5c8\ub294\ub370, 5.3 \uc5d0\uc11c \uac1c\uc120\ub418\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uad73\uc774 `ts-pattern`\uc744 \uc4f8 \uc774\uc720\ub294 \uc5c6\uc5b4 \ubcf4\uc778\ub2e4.\\n- [Typescript \ub9b4\ub9ac\uc988 \ub178\ud2b8 \ub9c1\ud06c](https://www.typescriptlang.org/play?#code/MYewdgzgLgBAlhAqmAJgUwGZzGlMC8MAFAIYBcMJYAngJQUnwQwCuqm2uBAfJQfoTbosOFACgxUagAc0MAPIAjAFYEYAbzExtMYCQBOKAPwUAwgbwAfVuxG4xAXwnYoafRhLA55wxq07FKgBrExhofWwAcxhHCTFQSFgMNmA1IhAVCiVlWh4-HTCAdzgoYAALIih9FjRczQKCvQg5AEIEZGFOFHSVADo9Q3p8hpGE6BgMEBA1DOV+i38RxvAIEAAbNF61kEieuYGUWgBuRaXtRX00EiCTs6cz06cHIA)\\n\\n\uacb0\ub860\uc801\uc73c\ub85c \ub3c4\uc785\ud558\uc9c0 \uc54a\uae30\ub85c \uacb0\uc815\ud588\ub2e4.\\n\\n### \ubc1c\uacac\ud55c \uc0ac\uc774\ub4dc \ucf00\uc774\uc2a4\\n\\n`when()` \uc5d0 \ub118\uae30\ub294 \ud568\uc218\uc5d0\uc11c generic\uc744 \uc0ac\uc6a9\ud560 \uacbd\uc6b0 type narrowaing\uc774 \ub418\uc9c0 \uc54a\uc558\ub2e4.\\n\\n```ts\\n// \uc774\ub7f0 \ud568\uc218\ub4e4\uc744 \ub118\uaca8\ub3c4 \uc774\ud6c4 \ube14\ub7ed\uc5d0\uc11c val\uc774 T\uac00 \uc544\ub2cc T | undefined\ub85c \ucd94\ub860\ub418\uc5c8\ub2e4.\\nconst isDefined = <T>(val: T | undefined): val is T => {\\n  return val !== undefined;\\n};\\n```\\n\\n[issue](https://github.com/gvergnaud/ts-pattern/issues/64)\ub3c4 \uc0dd\uc131\ub418\uc5b4 \uc788\ub294\ub370 \ub2f9\uc7a5 \ud574\uacb0\uc740 \uc5b4\ub824\uc6b4 \ub4ef \ud558\ub2e4.\\n\\n\uc7ac\ubc0c\uc5c8\uc9c0\ub9cc \uc804\uccb4\uc801\uc73c\ub85c \uc870\uae08 \uc544\uc26c\uc6b4 \ub3c4\uad6c\ub77c\uace0 \ub290\uaf08\ub2e4."},{"id":"use-effect-with-funnel","metadata":{"permalink":"/blog/use-effect-with-funnel","source":"@site/blog/2023-06-16-use-effect-with-funnel.md","title":"useEffect cleanup\uacfc \ud37c\ub110 \uad00\ub9ac\uc5d0 \ub300\ud55c \uc0dd\uac01\ub4e4","description":"\uac1c\uc694","date":"2023-06-16T00:00:00.000Z","formattedDate":"2023\ub144 6\uc6d4 16\uc77c","tags":[{"label":"react","permalink":"/blog/tags/react"},{"label":"useEffect","permalink":"/blog/tags/use-effect"}],"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"use-effect-with-funnel","title":"useEffect cleanup\uacfc \ud37c\ub110 \uad00\ub9ac\uc5d0 \ub300\ud55c \uc0dd\uac01\ub4e4","tags":["react","useEffect"]},"unlisted":false,"prevItem":{"title":"ts-pattern\uc744 \ub3c4\uc785\ud574\ub3c4 \ub420\uae4c?","permalink":"/blog/can-i-use-ts-pattern"},"nextItem":{"title":"\uccab \uc81c\ud488\uc744 \ubc30\ud3ec\ud558\uace0 \ubc30\uc6b4 \uac83\ub4e4","permalink":"/blog/after-launching-first-product"}},"content":"### **\uac1c\uc694**\\n\\n\ud37c\ub110\uc744 \uc774\ud0c8\ud560 \ub54c, \uc720\uc800\uac00 \uc5c5\ub370\uc774\ud2b8\ud55c \uc0c1\ud0dc\ub97c \ucd08\uae30 \uc0c1\ud0dc\ub85c clear\ud558\ub294 \uc694\uad6c\uc0ac\ud56d\uc5d0 \ub300\ud574\uc11c \uace0\ubbfc\ud558\ub2e4\uac00 \uc0dd\uac01\ud55c \ub0b4\uc6a9\ub4e4\uc744 \uc815\ub9ac\ud574\ubcf4\uc558\ub2e4.\\n\\n### **TL;DR**\\n\\n- useEffect \ub0b4\uc758 \ucf54\ub4dc \uc5c6\uc774 cleanup\ub9cc \uc815\uc758\ub41c \ucf54\ub4dc\ub294 \ubb38\uc81c\uac00 \ub420 \uc218 \uc788\ub2e4.\\n- \ucef4\ud3ec\ub10c\ud2b8\uc758 side effect\ub294 \uac00\ub2a5\ud558\ub2e4\uba74 \uc774\ubca4\ud2b8\uc640 \uc774\ubca4\ud2b8 \ud578\ub4e4\ub7ec\ub85c \uad00\ub9ac\ud558\uace0, useEffect\ub294 \uadf8\ub807\uac8c \ud558\uae30 \uc5b4\ub824\uc6b8 \ub54c\ub9cc \uc0ac\uc6a9\ud55c\ub2e4.\\n\\n\x3c!-- \ubaa8\ubc14\uc77c \ucee4\uba38\uc2a4 \uc571\uc758 \uacb0\uc81c \ud398\uc774\uc9c0\uc5d0\uc11c \ub2e4\uc74c\uacfc \uac19\uc740 \uc0c1\ud669\uc744 \uc608\uc2dc\ub85c \uc0dd\uac01\ud574\ubcf4\uc790.\\n\\n\\\\- **\uacb0\uc81c \uc218\ub2e8 \uc9c0\uc815 \ud398\uc774\uc9c0** \\\\[**\ubaa8\ubc14\uc77c \ud074\ub77c\uc774\uc5b8\ud2b8 \uc571**\\\\]\\n\\\\- **\uacb0\uc81c \ud398\uc774\uc9c0** \\\\[**\uc6f9**\\\\]\\n\xa0 - **\uacb0\uc81c \ud398\uc774\uc9c0**\ub294 \uacb0\uc81c \uc218\ub2e8 \ub370\uc774\ud130\ub97c Next.js\uc758 getServerSideProps\uc5d0\uc11c Redux\uc758 initial store\ub85c \ucd08\uae30\ud654\uc2dc\ud0a4\uace0 \uc788\ub2e4.\\n\\n1.  **\ud074\ub77c\uc774\uc5b8\ud2b8 \uc571**\uc5d0\uc11c \\"**\uacb0\uc81c \uc218\ub2e8 \uc9c0\uc815 \ud398\uc774\uc9c0**\\"\ub97c \ub744\uc6b4\ub2e4.\\n2.  \uc720\uc800\uac00 \uacb0\uc81c \uc218\ub2e8\uc744 \uc9c0\uc815\ud55c\ub2e4.\\n3.  \ud074\ub77c\uc774\uc5b8\ud2b8\ub294 \\"**\uacb0\uc81c \ud398\uc774\uc9c0**\\" **\uc6f9\ubdf0** \ud654\uba74\uc744 \ub744\uc6cc\uc900\ub2e4. (\ud074\ub77c\uc774\uc5b8\ud2b8-\uc6f9\ubdf0 \uac04 \ub370\uc774\ud130 \uc804\ub2ec \ubc29\uc2dd\uc5d0 \ub300\ud574\uc11c\ub294 \uc0dd\uac01\ud558\uc9c0 \uc54a\ub294\ub2e4)\\n4.  \ucd5c\uc885 \uacb0\uc81c\uac00 \uc774\ub8e8\uc5b4\uc9c0\uc9c0 \uc54a\uace0 **\uc6f9** \\"**\uacb0\uc81c \ud398\uc774\uc9c0\\"**\ub97c \uc774\ud0c8\ud55c\ub2e4.\\n5.  \uc6f9\ubdf0\uc5d0\uc11c **\uc6f9** \\"**\uba54\uc778 \ud398\uc774\uc9c0**\\"\ub85c \uc774\ub3d9\ud55c\ub2e4.\\n\\n\uc774 \ub54c, \ucd5c\uc885 \uacb0\uc81c\uac00 \uc774\ub8e8\uc5b4\uc9c0\uc9c0 \uc54a\uc558\uc73c\ubbc0\ub85c, 5\ubc88\uc758 \uc6f9 \\"**\uba54\uc778 \ud398\uc774\uc9c0**\\"\uc5d0\uc11c **\uacb0\uc81c \uc218\ub2e8\uc774 \uc9c0\uc815\ub418\uc9c0 \uc54a\uc740** **\uc0c1\ud0dc**\uc5ec\uc57c \ud558\ub294 \uc694\uad6c\uc0ac\ud56d\uc774 \uc788\ub2e4\uace0 \uac00\uc815\ud558\uc790. \uc989, clear \ub41c \uc0c1\ud0dc\uc5ec\uc57c \ud55c\ub2e4.\\n\\n\uc774\ub7f0 \uacbd\uc6b0 \uacb0\uc81c \uc218\ub2e8 \uc815\ubcf4\ub97c clear\ud558\ub294 \ub3d9\uc791\uc740 \uc5b4\ub5bb\uac8c \uad00\ub9ac\ud574\uc57c \ud560\uae4c? --\x3e\\n\\n### **cleanup\uc744 \ud1b5\ud55c \uc194\ub8e8\uc158**\\n\\n\uac00\uc7a5 \uba3c\uc800 \ub5a0\uc62c\ub9b0 \ubc29\ubc95\uc740 \ud398\uc774\uc9c0 \ucef4\ud3ec\ub10c\ud2b8\uc758 cleanup \uc2dc\uc810\uc5d0 clear\uc2dc\ucf1c\uc8fc\ub294 \ubc29\ubc95\uc774\ub2e4.\\n\\n```tsx\\nfunction Payment() {\\n  // ...\\n\\n  useEffect(() => {\\n    return () => {\\n      dispatch(paymentActions.clear());\\n    };\\n  }, []);\\n\\n  // ...\\n}\\n```\\n\\n\ubb38\uc81c\ub294 \uac1c\ubc1c \ud658\uacbd\uc5d0\uc11c **StrictMode** \ub3d9\uc791\uc73c\ub85c \uc778\ud574 \ucef4\ud3ec\ub10c\ud2b8\uac00 \ub450 \ubc88 \ub80c\ub354\ub418\uba70 cleanup\uc774 \ud55c \ubc88 \uc2e4\ud589\ub41c\ub2e4\ub294 \uc810\uc774\ub2e4. \uacb0\uacfc\uc801\uc73c\ub85c \uc6d0\ud558\ub294 \uac12\uc73c\ub85c \uc720\uc9c0\uc2dc\ud0ac \uc218 \uc5c6\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud55c\ub2e4.\\n\\n\uc774\uc5d0 \ub300\ud55c \ud574\uacb0\ucc45\uc73c\ub85c \ub450 \uac00\uc9c0\ub97c \uc0dd\uac01\ud588\ub2e4.\\n\\n1.  \ud658\uacbd\ubcc0\uc218\ub97c \ucc38\uc870\ud558\ub294 \ub4f1(dev\uc778\uc9c0 \ud655\uc778\ud558\uace0 \ubd84\uae30) \uc5b4\ub5bb\uac8c\ub4e0 cleanup\uc744 \ub2e8 \ud55c \ubc88 \uc2e4\ud589\uc2dc\ud0a8\ub2e4.\\n2.  useEffect\uac00 \uc544\ub2cc \ub2e4\ub978 \ubc29\ubc95\uc744 \ud1b5\ud574 \uad6c\ud604\ud55c\ub2e4.\\n\\n### **cleanup\uc774 \uc194\ub8e8\uc158\uc774 \ub420 \uc218 \uc5c6\ub294 \uc774\uc720**\\n\\nuseEffect cleanup\uc774 \uc194\ub8e8\uc158\uc774 \ub420 \uc218 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ud55c \uc774\uc720\ub294 \uadf8 **\uba58\ud0c8\ubaa8\ub378\uc774 \ub3d9\uae30\ud654\ub97c \uad6c\ud604\ud558\ub294 \uac83** \uc774\uae30 \ub54c\ubb38\uc774\ub2e4. \uc694\uad6c\uc0ac\ud56d\uc740 \uc720\uc800\uac00 \ud2b9\uc815 \ud37c\ub110\uc5d0 \uc9c4\uc785\ud558\uac70\ub098, \uc5b4\ub5a4 \ubc84\ud2bc\uc744 \ub204\ub974\ub294 \ub4f1\uc758 \ub3d9\uc791\uc744 \ud588\uc744 \ub54c \uadf8\uc5d0 \ubc18\uc751\ud558\ub294 \ub3d9\uc791\uc744 \uc815\uc758\ud558\uba74 \ub420 \ubfd0\uc774\ub2e4. \uc989, **\ub2e8\uc21c\ud788 \ud654\uba74\uc5d0\uc11c \uc0ac\ub77c\uc9c8 \ub54c\ub97c \uce90\uce58\ud558\uae30 \uc704\ud574 cleanup\uc744 \uc0ac\uc6a9\ud558\ub294 \uac83\uc740 \uc644\uc804 \uc798\ubabb\ub41c \uc0ac\uace0\ubc29\uc2dd\uc77c \uc218 \uc788\ub2e4**.\\n\\n\x3c!--\\n- **StrictMode** \ub3d9\uc791\uc5d0\ub3c4 \uc77c\uad00\ub41c \uacb0\uacfc\uac00 \ub80c\ub354\ub418\ub294, \ubcf8\ub514 \uc774\ud399\ud2b8\uac00 \uc5ec\ub7ec\ubc88 \uc2e4\ud589\ub418\uc5b4\ub3c4 \uad1c\ucc2e\uc740 \ud615\ud0dc\ub85c \uad6c\ud604\ub418\uc5b4\uc57c \ud558\ub294 \uac83\uc774 \uc633\ub2e4\\n- \ub9cc\uc57d **cleanup\ub9cc \uc788\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \uadf8\uc5d0 \ud398\uc5b4\ub9c1\ub418\ub294 useEffect \uc5d0\uc11c \ucd08\uae30\ud654 \uad6c\ubb38\uc774 \uc788\uc5c8\ub2e4\uba74 \uc194\ub8e8\uc158\uc77c \uac00\ub2a5\uc131\uc774 \uc788\uc5c8\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4.** --\x3e\\n\\n\x3c!-- > \uacb0\uc81c \ud398\uc774\uc9c0\ub294 \ud074\ub77c\uc774\uc5b8\ud2b8\uc5d0\uc11c \uc804\ub2ec\ubc1b\uc740 \uacb0\uc81c \uc218\ub2e8 \ub370\uc774\ud130\ub97c Next.js\uc758 getServerSideProps\uc5d0\uc11c Redux\uc758 initial store\ub85c \ucd08\uae30\ud654\uc2dc\ud0a4\uace0 \uc788\ub2e4. --\x3e\\n\\n\x3c!-- \ub2e4\ub9cc \uc774 \uae00 \ucd08\ubc18\uc5d0 \uc5b8\uae09\ub41c \uac83\uacfc \uac19\uc774 \uc11c\ubc84\uc0ac\uc774\ub4dc\uc5d0 \ucd08\uae30\ud654 \uad6c\ubb38\uc774 \uc788\uc73c\ubbc0\ub85c, \ud074\ub77c\uc774\uc5b8\ud2b8 \uc0ac\uc774\ub4dc\uc758 useEffect\uc5d0 \ucd08\uae30\ud654 \uad6c\ubb38\uc774 \uc704\uce58\ub418\uc9c0 \uc54a\uc73c\ubbc0\ub85c **cleanup\uc740 \uc801\uc808\ud55c \uc194\ub8e8\uc158\uc774 \uc544\ub2c8\ub77c\uace0 \uc0dd\uac01\ud588\ub2e4.** --\x3e\\n\\n\uadf8\ub807\ub2e4\uba74 \ub2e4\ub978 \ud574\uacb0\ucc45\uc740 \ubb34\uc5c7\uc774 \uc788\uc744\uae4c?\\n\\n\uae30\ubcf8\uc801\uc73c\ub85c \ub9ac\uc561\ud2b8\uc5d0\uc11c [\ucef4\ud3ec\ub10c\ud2b8\ub294 \ub80c\ub354\uc5d0 \ub300\ud574\uc11c \ud4e8\uc5b4\ud574\uc57c \ud55c\ub2e4](https://react.dev/learn/keeping-components-pure#where-you-_can_-cause-side-effects). \uacf5\uc2dd\ucc98\ub7fc \uacc4\uc0b0\ub9cc \ud574\uc57c\ud558\uc9c0, \ubcc0\uacbd\ud574\uc11c\ub294 \uc548\ub41c\ub2e4.\\n\\n```tsx\\n<Component\xa0props={1}\xa0/>\xa0\\n<Component\xa0props={1}\xa0/>\xa0//\xa0\uac19\uc740\xa0input\xa0(props,\xa0state,\xa0context)\xa0\uc5d0\xa0\ub300\ud574\uc11c\xa0\uac19\uc740\xa0\uacb0\uacfc\ub97c\xa0\ub9ac\ud134\ud574\uc57c\xa0\ud55c\ub2e4\\n```\\n\\n\ub2e8, \ub80c\ub354 \uc774\ud6c4\uc5d0 side effect\uac00 \ud544\uc694\ud55c \uc2dc\uc810\uc774 \uc788\ub2e4. \uc774\ub7f0 side effect\uc5d0\ub294 \ub370\uc774\ud130\ub97c \ubc14\uafb8\uac70\ub098 API\ub97c \uc694\uccad\ud558\ub294 \ub4f1 \ub2e4\uc591\ud55c \ud589\ub3d9\uc774 \uc788\uc744 \uc218 \uc788\ub2e4. \uc774\ub7ec\ud55c side effect\ub294 \ub300\ubd80\ubd84\xa0\uc774\ubca4\ud2b8\xa0\ud578\ub4e4\ub7ec\ub85c\xa0\ucc98\ub9ac\uac00\xa0\uac00\ub2a5\ud558\ub2e4.\\n**\uc774\ubca4\ud2b8\ub294 \ub80c\ub354 \uc911\uc5d0 \uc77c\uc5b4\ub098\uc9c0 \uc54a\uc73c\ubbc0\ub85c** \ud4e8\uc5b4\ud560 \ud544\uc694\uac00 \uc5c6\ub2e4.\\n\\n> Even though event handlers are defined inside your component, they don\u2019t run during rendering! **So event handlers don\u2019t need to be pure.**\\n\\n\ubc1c\uc0dd\uc2dc\ucf1c\uc57c \ud558\ub294 side effect\uac00 \uadf8 \uc5b4\ub5a4 \uc774\ubca4\ud2b8 \ud578\ub4e4\ub7ec\ub85c\ub3c4 \ucc98\ub9ac\uac00 \uc5b4\ub835\ub2e4\uba74, \ub9c8\uc9c0\ub9c9 \uc635\uc158\uc73c\ub85c useEffect\ub97c \ud65c\uc6a9\ud55c\ub2e4.\\n\\n\uacb0\ub860\uc801\uc73c\ub85c, \uc6b0\ub9ac\uac00 \uc81c\uc5b4\ud558\uace0 \uc2f6\uc740 \uc0c1\ud669\uc740 **\uc774\ubca4\ud2b8\ub2e4**. \ud37c\ub110\uc744 \uc774\ud0c8\ud558\ub294 \ub3d9\uc791\uc740 \ub4a4\ub85c \uac00\uae30 \ubc84\ud2bc\uc744 \ud074\ub9ad\ud558\ub294 \uac83 \ucc98\ub7fc \uc774\ubca4\ud2b8\ub85c\uc11c \ucc98\ub9ac\ub420 \uc218 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4.\\n\\n---\\n\\n### **\ub370\uc774\ud130\ub97c \uc5b4\ub5bb\uac8c \uad00\ub9ac\ud560 \uac83\uc778\uac00?**\\n\\n\ud55c \ud37c\ub110\uc5d0 \ud574\ub2f9\ud558\ub294 \ucf54\ub4dc\ub4e4\uc744 \ud558\ub098\uc758 \ubaa8\ub4c8\uc5d0 \uc704\uce58\uc2dc\ucf1c **\uc751\uc9d1\ub3c4\ub97c \ub192\uc5ec\uc57c \uc804\uccb4\uc801\uc778 \ub370\uc774\ud130 \ud750\ub984\uc774 \uad00\ub9ac\ub420 \uc218** \uc788\ub2e4\uace0 \uc0dd\uac01\ud588\ub2e4.\\n\\n**\uc774\ubca4\ud2b8 \ud578\ub4e4\ub7ec\ub85c \ud074\ub9ac\uc5b4\ud558\ub294 \ub85c\uc9c1\ub4e4\uc774 \uacf3\uacf3\uc5d0 \uc0b0\uc7ac\ub418\uc5b4 \uc788\uc73c\uba74 \uc804\uc5ed \uc0c1\ud0dc\uc5d0 \ub300\ud55c \uad00\ub9ac\uac00 \ud798\ub4e4\uc5b4\uc9c4\ub2e4.** \uc5b4\ub5a4 \ud37c\ub110\uc5d0\uc11c\ub294 \uc5b4\ub5a4 \ub370\uc774\ud130\ub97c \ud074\ub9ac\uc5b4\ud574\uc57c\ud558\uace0, \uadf8 \ud6c4 \uc9c4\uc785 \uc2dc\uc5d0\ub294 \uc5b4\ub5a4 \ub370\uc774\ud130\uac00 \ub0a8\uc544 \uc788\uc744 \uac83\uc774\uace0.. \uc774\ub7f0 \uac83\ub4e4\uc744 \uc0dd\uac01\ud558\uba74 \uc0c1\ud0dc\ub97c \ucd94\uc801\ud558\uae30\uc5d0 \uc5b4\ub824\uc6c0\uc774 \uc788\uc744 \uc218 \uc788\ub2e4.\\n\\ntoss\uc758 slash \ub77c\uc774\ube0c\ub7ec\ub9ac\uc5d0 [useFunnel](https://slash.page/ko/libraries/react/use-funnel/readme.i18n/)\ub77c\ub294 hook\uc774 \uc788\ub2e4, \uc774\ucc98\ub7fc \ud55c \ud37c\ub110\uc5d0 \ud574\ub2f9\ud558\ub294 \uc2a4\ud15d\ub4e4\uc744 \uad00\ub9ac\ud558\ub294 \ubc29\ubc95\ub3c4 \uc88b\uc740 \uac83 \uac19\ub2e4.\\n\\n```tsx\\nconst KyoboLifeFunnel = () => {\\n  const [Funnel, state, setState] = useFunnel([\'\uc544\ud30c\ud2b8\uc5ec\ubd80\', \'\uc9c0\uc5ed\uc120\ud0dd\', \'\uc644\ub8cc\'] as const).withState<{\\n    propertyType?: \'\ube4c\ub77c\' | \'\uc544\ud30c\ud2b8\';\\n    address?: string;\\n  }>({});\\n\\n  const \uc0c1\ub2f4\uc2e0\uccad = useLoanApplicationCallback();\\n\\n  return (\\n    <Funnel>\\n      <Funnel.Step name=\\"\uc544\ud30c\ud2b8\uc5ec\ubd80\\">\\n        <\uc544\ud30c\ud2b8\uc5ec\ubd80\uc2a4\ud15d \uc9c0\uc5ed\uc120\ud0dd\uc73c\ub85c\uac00\uae30={() => setState(prev => ({...prev, step: \'\uc9c0\uc5ed\uc120\ud0dd\', isApartment: true})} />\\n      </Funnel.Step>\\n      <Funnel.Step name=\\"\uc9c0\uc5ed\uc120\ud0dd\\">\\n        <\uc9c0\uc5ed\uc120\ud0dd\uc2a4\ud15d \uc9c0\uc5ed\uc120\ud0dd\uc644\ub8cc={(\uc9c0\uc5ed\uc815\ubcf4) => setState(prev => ({...prev, step: \'\uc644\ub8cc\', region: \uc9c0\uc5ed\uc815\ubcf4})} />\\n      </Funnel.Step>\\n      <Funnel.Step name=\\"\uc644\ub8cc\\">\\n        <\uc644\ub8cc\uc2a4\ud15d \uc2e0\uccad={() => \uc0c1\ub2f4\uc2e0\uccad(state)} />\\n      </Funnel.Step>\\n    </Funnel>\\n  );\\n};\\n```"},{"id":"after-launching-first-product","metadata":{"permalink":"/blog/after-launching-first-product","source":"@site/blog/2023-04-24-after-launching-first-product.md","title":"\uccab \uc81c\ud488\uc744 \ubc30\ud3ec\ud558\uace0 \ubc30\uc6b4 \uac83\ub4e4","description":"\uc57d 3\uac1c\uc6d4 \uc815\ub3c4 \uac1c\ubc1c\uc5d0 \ucc38\uc5ec\ud55c \uc81c\ud488\uc774 3\uc6d4 \uacbd \ub4dc\ub514\uc5b4 \uc138\uc0c1\uc5d0 \ub098\uc654\ub2e4.","date":"2023-04-24T00:00:00.000Z","formattedDate":"2023\ub144 4\uc6d4 24\uc77c","tags":[{"label":"career","permalink":"/blog/tags/career"},{"label":"retrospect","permalink":"/blog/tags/retrospect"}],"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"after-launching-first-product","title":"\uccab \uc81c\ud488\uc744 \ubc30\ud3ec\ud558\uace0 \ubc30\uc6b4 \uac83\ub4e4","tags":["career","retrospect"]},"unlisted":false,"prevItem":{"title":"useEffect cleanup\uacfc \ud37c\ub110 \uad00\ub9ac\uc5d0 \ub300\ud55c \uc0dd\uac01\ub4e4","permalink":"/blog/use-effect-with-funnel"},"nextItem":{"title":"\ub0b4\uac00 2022\ub144\uc5d0 \ubc30\uc6b4 \uac83\ub4e4","permalink":"/blog/2022-retro"}},"content":"\uc57d 3\uac1c\uc6d4 \uc815\ub3c4 \uac1c\ubc1c\uc5d0 \ucc38\uc5ec\ud55c \uc81c\ud488\uc774 3\uc6d4 \uacbd \ub4dc\ub514\uc5b4 \uc138\uc0c1\uc5d0 \ub098\uc654\ub2e4.\\n\\n\uc8fc\uc694 \uc81c\ud488\uc774\ub77c\uace0 \ud560 \ub9cc\ud55c \uc815\ub3c4\ub85c \ud06c\uae30\uac00 \ud070 \uacbd\uc6b0\ub294 \ucc98\uc74c\uc778 \uac83 \uac19\ub2e4.\\n\\n\ud68c\uc0ac\uc5d0\uc11c \uc6b4\uc601 \uc911\uc778 \uc11c\ube44\uc2a4\uc758 \ud2b9\uc9d5\uc73c\ub85c\ub294 \ud22c \uc0ac\uc774\ub4dc \ub9c8\ucf13 \ud50c\ub7ab\ud3fc \uc11c\ube44\uc2a4\ub77c\ub294 \uc810\uc774 \uc788\ub2e4. \uc790\uc5f0\uc2a4\ub7fd\uac8c \uc5b4\ub5a4 \ud55c \uc81c\ud488\uc744 \uac1c\ubc1c\ud55c\ub2e4\ub294 \uac83\uc740 \uc218\uc694\uc790(\uc774\ud6c4 \'\uc720\uc800\') \uc0ac\uc774\ub4dc\uc5d0 \uc81c\uacf5\ub418\ub294 \uc11c\ube44\uc2a4\uc640, \uacf5\uae09\uc790(\uc774\ud6c4 \'\ub4dc\ub77c\uc774\ubc84\') \uc0ac\uc774\ub4dc\uc5d0 \uc81c\uacf5\ub418\ub294 \uc11c\ube44\uc2a4\ub97c \ub3d9\uc2dc\uc5d0 \uac1c\ubc1c\ud558\uac8c \ub418\ub294 \uacbd\uc6b0\uac00 \ub9ce\ub2e4. \ub610\ud55c \ud604\uc7ac \ubaa9\uc801 \uc870\uc9c1 \ud615\ud0dc\ub85c \uc77c\ud558\uace0 \uc788\ub294\ub370, \uc774\ub7ec\ud55c \ud2b9\uc9d5\ub4e4 \ub355\ubd84\uc5d0 \uac1c\ubc1c\uc790\ub85c\uc11c\ub294 \uc7ac\ubbf8\uc788\ub294 \uacbd\ud5d8\uc744 \ud560 \uc218 \uc788\ub2e4.\\n\\n- \uc720\uc800-\ub4dc\ub77c\uc774\ubc84 \uc0ac\uc774\ub4dc\ub97c \ubaa8\ub450 \uace0\ub824\ud558\uba70 \uc81c\ud488\uc758 \uc544\uc774\ub514\uc5d0\uc774\uc158\ubd80\ud130 UI / UX \uac1c\uc120, \uc804\ubc18\uc801\uc778 \uc0ac\uc6a9 \uacbd\ud5d8\uae4c\uc9c0 \ud3ed\ub113\uac8c \ucc38\uc5ec\ud560 \uc218 \uc788\uc5c8\ub2e4.\\n- \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub85c\uc11c\ub294 \uc0dd\uac01\ud574\uc57c \ud558\ub294 \uc0ac\uc6a9\uc790\uc758 \uacbd\ud5d8\uc774 \uc804\ud600 \ub2e4\ub974\ub2e4\ub294 \uc810\uc774 \uc7ac\ubc0c\uc5c8\ub2e4.\\n\\n\uc785\uc0ac \ud6c4 \uc0ac\uc2e4\uc0c1 \uccab \ud504\ub85c\uc81d\ud2b8\uc600\uace0, \ud504\ub860\ud2b8\uc5d4\ub4dc \uad00\ub828 \uac1c\ubc1c\uc740 \ub300\uccb4\ub85c \ud63c\uc790 \uc9c4\ud589\ud588\ub2e4. \ucd08\ubc18\uc5d0\ub294 \uc0dd\uac01\ud588\ub358 \uac83\ubcf4\ub2e4 \ud06c\uae30\uac00 \ud070 \uc81c\ud488\uc774\uc5b4\uc11c \ub2f9\ud669\uc2a4\ub7fd\uae30\ub3c4 \ud588\uc9c0\ub9cc, \uac19\uc774 \uc791\uc5c5\ud55c \ub3d9\ub8cc\ub4e4\uc758 \ub3c4\uc6c0 \ub355\ubd84\uc5d0 \uc798 \ub9c8\ubb34\ub9ac\ud560 \uc218 \uc788\uc5c8\ub2e4. \ud504\ub85c\ub85c\uc11c \ucc98\uc74c\uc73c\ub85c \ub0b4\ub193\uc740 \ubcfc\ub968 \uc788\ub294 \uacb0\uacfc\ubb3c\uc774\uc790, \uadf8 \uacfc\uc815\uc5d0 \uc8fc\ub3c4\uc801\uc73c\ub85c \ucc38\uc5ec\ud560 \uc218 \uc788\ub294 \uc810\uc774 \uc758\ubbf8 \uc788\uc5c8\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \ubc30\ud3ec \uc774\ud6c4 \ud070 \uc774\uc288 \uc5c6\uc774 \uc815\uc0c1\uc801\uc73c\ub85c \ud504\ub85c\ub355\uc158\uc5d0\uc11c \uc6b4\uc601 \uc911\uc778 \uc810\ub3c4 \ud504\ub85c\uc81d\ud2b8\ub85c\uc11c \uc88b\uc740 \ub9c8\ubb34\ub9ac\uc600\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4.\\n\\n\uc544\uc26c\uc6e0\ub358 \uc810\uc740 \ub2e4\uc74c\ucc98\ub7fc \uc815\ub9ac\ud574 \ubcfc \uc218 \uc788\uaca0\ub2e4.\\n\\n- \uc608\uc0c1 \uac1c\ubc1c \uc2dc\uac04 \uc0b0\uc815\uc774 \ubd80\uc815\ud655\ud588\ub2e4.\\n\\n  - \ucf54\ub4dc\ubca0\uc774\uc2a4\uc640 \uae30\uc874 \uc5c5\ubb34 \ud504\ub85c\uc138\uc2a4 \uc774\ud574\ub3c4\uac00 \ub2e4\uc18c \ubd80\uc871\ud55c \uc0c1\ud0dc\uc600\uae30\uc5d0 \uc0b0\uc815\ud588\ub358 \uac83\uacfc \uc2e4\uc81c \uac1c\ubc1c \ub9c8\ubb34\ub9ac \uc2dc\uac04\uc774 \ub2e4\ub978 \uacbd\uc6b0\uac00 \uaf64 \uc788\uc5c8\ub2e4.\\n  - \ud504\ub85c\uc81d\ud2b8 \uc774\ud574\ub3c4 \ubd80\uc871\uacfc, \uc5ed\ub7c9\uc744 \uc99d\uba85\ud558\uace0 \uc2f6\ub2e4\ub294 \uc695\uc2ec \ub54c\ubb38\uc5d0 \uc870\uae08 \ubb34\ub9ac\ud55c \uc77c\uc815\uc744 \uc7a1\uae30\ub3c4 \ud588\ub2e4. 1~2 \uc8fc\uc57c \uad1c\ucc2e\uc558\uc9c0\ub9cc \ud504\ub85c\uc81d\ud2b8 \uae30\uac04\uc774 \uae38\uc5b4\uc9c0\uc790 \uc870\uae08 \ubb34\ub9ac\uc600\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4e0\ub2e4.\\n\\n- \ub108\ubb34 \ud070 PR\uc744 \ub9cc\ub4e4\uc5c8\ub2e4.\\n  - \ucd08\ubc18\uc758 \ud504\ub85c\uc81d\ud2b8 \uc774\ud574\ub3c4 \ubd80\uc871\uc73c\ub85c \uc778\ud574 \uc791\uc5c5 \ub2e8\uc704\ub97c \ub108\ubb34 \ud06c\uac8c \ub098\ub234\uace0, \uc774\ub294 PR\uc774 \ub108\ubb34 \ucee4\uc9c0\ub294 \uacb0\uacfc\ub85c \uc774\uc5b4\uc84c\ub2e4. \ucf54\ub4dc \ub9ac\ubdf0\ud558\ub294 \ub3d9\ub8cc\ub4e4\ub3c4 \ubd80\ub2f4\uc2a4\ub7ec\uc6e0\uc744 \uac83\uc774\uace0, \uac08\uc218\ub85d \uc138\ubd80\uc801\uc778 \uc791\uc5c5\uc758 \ud30c\uc545\ub4e4\uc774 \uc27d\uc9c0 \uc54a\ub2e4\uace0 \ub290\uaf08\ub2e4.\\n- \uc6d0\uc778\uc740 \ubcf5\ud569\uc801\uc774\uaca0\uc9c0\ub9cc \uc0dd\uac01\ubcf4\ub2e4 \ub9ce\uc740 \uc218\uc694\ub97c \ub9cc\ub4e4\uc5b4 \ub0b4\uc9c0 \ubabb\ud588\ub2e4.\\n\\n\uc5b4\uca0c\ub4e0 \uc774 \uc81c\ud488\uc774 \uc131\uacf5\uc801\uc73c\ub85c \ub79c\ub529\ud55c \ub355\ubd84\uc5d0 \ub9ce\uc740 \uac83\uc744 \ubc30\uc6b8 \uc218 \uc788\uc5c8\ub2e4. \uacfc\uc815\uc5d0\uc11c \uacaa\uc740 \uc5b4\ub824\uc6c0\ub3c4 \uc788\uc5c8\uc9c0\ub9cc \uc804\ubcf4\ub2e4 \ud55c \ub2e8\uacc4 \uc131\uc7a5\ud588\uc74c\uc5d0 \uc758\uc2ec\uc740 \uc5c6\ub2e4."},{"id":"2022-retro","metadata":{"permalink":"/blog/2022-retro","source":"@site/blog/2023-02-12-2022-retro.md","title":"\ub0b4\uac00 2022\ub144\uc5d0 \ubc30\uc6b4 \uac83\ub4e4","description":"- \ud504\ub85c\ub85c\uc11c\uc758 \ub9c8\uc74c\uac00\uc9d0","date":"2023-02-12T00:00:00.000Z","formattedDate":"2023\ub144 2\uc6d4 12\uc77c","tags":[{"label":"career","permalink":"/blog/tags/career"},{"label":"retrospect","permalink":"/blog/tags/retrospect"}],"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"2022-retro","title":"\ub0b4\uac00 2022\ub144\uc5d0 \ubc30\uc6b4 \uac83\ub4e4","tags":["career","retrospect"]},"unlisted":false,"prevItem":{"title":"\uccab \uc81c\ud488\uc744 \ubc30\ud3ec\ud558\uace0 \ubc30\uc6b4 \uac83\ub4e4","permalink":"/blog/after-launching-first-product"}},"content":"- \ud504\ub85c\ub85c\uc11c\uc758 \ub9c8\uc74c\uac00\uc9d0\\n\\n2022\ub144\uc740 \ub3c8\uc744 \ubc1b\uace0 \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uac1c\ubc1c\uc790\ub85c \uc77c\ud558\uae30 \uc2dc\uc791\ud55c \uccab \ud574\ub2e4. \ud559\uc0dd \uaf2c\ub9ac\ud45c\ub97c \ub5bc\uace0 \ucc98\uc74c\uc73c\ub85c \ud68c\uc0ac\uc5d0\uc11c \uc77c\uc744 \ud558\uba70 \ucc38 \ub9ce\uc774 \ubc30\uc6e0\ub2e4\uace0 \uc0dd\ud55c\ub2e4.\\n\\n\uc5b4\ub5a4 \ud0dc\ub3c4\ub85c \uc77c\uc744 \ub300\ud558\uace0, \uc0ac\ub78c\ub4e4\uc744 \ub300\ud560\uc9c0\uc5d0 \ub300\ud55c \ub098\ub9cc\uc758 \uae30\uc900\uc744 \ubc30\uc6cc\ub098\uac08 \uc218 \uc788\uc5c8\ub2e4. \ub3c8\uc744 \ubc1b\uace0 \ud68c\uc0ac\uc5d0\uc11c \uc77c\uc744 \ud558\ub294 \ud55c \ud504\ub85c\ub85c\uc11c\uc758 \ud0dc\ub3c4\ub97c \uc783\uc9c0 \uc54a\uc73c\ub824\uace0 \ub178\ub825\ud588\ub2e4.\\n\\n---\\n\\n- \uc774\uc9c1\\n\\n\ube44\uad50\uc801 \uc77c\ucc0d \uc774\uc9c1 \ud504\ub85c\uc138\uc2a4\ub97c \uacbd\ud5d8\ud558\uba70 \ub9ce\uc740 \uacbd\ud5d8\uacfc \uace0\ubbfc\uc744 \ud560 \uc218 \uc788\uc5c8\ub2e4. \uc5ec\ub2f4\uc774\uc9c0\ub9cc \uc0c8 \uc9c1\uc7a5 \uccab \ucd9c\uadfc \uc804\uc5d0 \ud63c\uc790 \ub2e4\ub140\uc628 \uc5ec\ud589 \ub355\ubd84\uc5d0 \uc18c\uc911\ud55c \uacbd\ud5d8\uc744 \ud560 \uc218 \uc788\uc5c8\uace0, \uc778\uac04\uc801\uc73c\ub85c\ub3c4 \uc131\uc7a5\ud560 \uc218 \uc788\uc5c8\ub2e4. \uadf8\ub807\uae30\uc5d0 \'2022\ub144\uc758 \uc0ac\uac74 \ub2e8 \ud558\ub098\'\ub97c \uc120\uc815\ud55c\ub2e4\uba74 \uc774\uc9c1\uc744 \uaf3d\uc544\uc57c\uaca0\ub2e4.\\n\\n\uc11c\ub958\ub97c \uc5ec\ub7ec\ubc88 \uace0\uce58\uba70 \uc774\ub825\uc11c\ub97c \uc5b4\ub5bb\uac8c \uc791\uc131\ud574\uc57c\ud558\ub294\uc9c0 \uace0\ubbfc\uc744 \ub9ce\uc774 \ud588\ub2e4. \uacbd\ub825\uc5d0\uc11c \uc5b4\ud544\ud560 \ub0b4\uc6a9\uc740 \uc5c6\uc5c8\uae30\uc5d0, \uc804 \uc9c1\uc7a5\uc5d0\uc11c \ub9e1\uc740 \ud504\ub85c\uc81d\ud2b8\ub4e4\uc5d0\uc11c \uc81c\uac00 \ud588\ub358 \uae30\uc220\uc801\uc778 \ud310\ub2e8\ub4e4\uc758 \uadfc\uac70 \uc704\uc8fc\ub85c \uc791\uc131\ub2e4. \ucd5c\uc885 \uc774\ub825\uc11c\ub85c \ud604 \uc9c1\uc7a5\uc5d0 \ucd5c\uc885 \ud569\uaca9 \ud6c4 \uc774\uc57c\uae30\ud574 \ubcf4\ub2c8 \uc774\ub825\uc11c\uc5d0 \ud655\uc2e4\ud55c \ud50c\ub7ec\uc2a4 \uc694\uc778\uc740 \uc5c6\uc5c8\ub354\ub77c\ub3c4 \ub9c8\uc774\ub108\uc2a4\ub294 \ub531\ud788 \uc5c6\uc5c8\ub358 \uac83 \uac19\ub2e4. \uc774\ub825\uc11c\ub780 \uacb0\uad6d \uba74\uc811 \uae30\ud68c\ub97c \uc5bb\uae30 \uc704\ud55c \uc218\ub2e8 \uc815\ub3c4\ub85c\ub9cc \uc0dd\uac01\ud55c\ub2e4\uba74 \uc131\uacf5\uc774\uc9c0 \uc54a\uc558\ub098 \uc2f6\ub2e4.\\n\\n\uacfc\uc81c, \uc804\ud654\uba74\uc811\ubd80\ud130 \ub77c\uc774\ube0c \ucf54\ub529\uae4c\uc9c0 \ub9ce\uc740 \uc804\ud615\uc744 \uacbd\ud5d8\ud588\ub2e4 \uba74\uc811\uc740 \ud655\uc2e4\ud788 \ud558\uba74 \ud560\uc218\ub85d \ub290\ub294 \uac83 \uac19\ub2e4. \ud2b9\ud788 \ub77c\uc774\ube0c \ucf54\ub529\uc740 \ud55c \ubc88 \ud574\ubcf4\uace0 \uc548\ud574\ubcf4\uace0\uc758 \ucc28\uc774\uac00 \uad49\uc7a5\ud788 \ud06c\ub2e4\uace0 \ub290\uaf08\ub2e4. \uceec\uccd0\ud54f \ub0b4\uc9c0 \uc778\uc131 \uba74\uc811\uc740 \uafb8\uba70\uc9c4 \ub2f5\ubcc0\uc744 \ud558\uae30\ubcf4\ub2e4\ub294 \uc790\uc2e0\uc758 \uc0dd\uac01\uc744 \uc774\uc57c\uae30\ud558\ub294\uac8c \uba74\uc811\uad00\ub3c4, \uba74\uc811\uc790\ub3c4 \ub354 \uc88b\ub2e4\uace0 \ub290\uaf08\ub2e4.\\n\\n---\\n\\n- \uc194\uc9c1\ud55c \ucee4\ubba4\ub2c8\ucf00\uc774\uc158\\n\\n\ud604 \uc9c1\uc7a5\uc758 \uae30\uc5c5 \ubb38\ud654\uc5d0\uc11c \ud06c\uac8c \uac15\uc870\ud558\ub294 \ub0b4\uc6a9 \uc911 \ud558\ub098\uac00 \uc194\uc9c1\ud55c \ucee4\ubba4\ub2c8\ucf00\uc774\uc158\uc774\ub2e4. \uc0ac\uc2e4 \uc785\uc0ac \uc804\uc5d0\ub294 \uac71\uc815\ub3c4 \uc788\uc5c8\ub294\ub370, \'\uc194\uc9c1\ud568\'\uacfc \'\ubb34\ub840\ud568\'\uc744 \uad6c\ubd84\ud558\uc9c0 \ubabb\ud558\ub294 \uc0ac\ub840\ub4e4\uc744 \uacaa\ub2e4 \ubcf4\ub2c8 \uacfc\uc5f0 \ub0b4\ubd80 \ubb38\ud654\uac00 \uc2e4\uc81c\ub85c \uc5b4\ub5a8\uc9c0 \uac71\uc815\uc774 \uc55e\uc130\ub2e4. \uadf8\ub807\uae30\uc5d0 \ucd5c\uc885 \uba74\uc811 \uc790\ub9ac\uc5d0\uc11c \uc870\uc9c1 \ub0b4\uc5d0\uc11c \uc5b4\ub5a4 \uc2dc\uac01\uc73c\ub85c \uc194\uc9c1\ud568\uc744 \ubc14\ub77c\ubcf4\uace0 \uc788\ub294\uc9c0 \uc9c8\ubb38\ud558\uae30\ub3c4 \ud588\ub2e4.\\n\\n\uc785\uc0ac \ud6c4 \ub290\ub080 \uc810\uc740 \uac71\uc815\uacfc\ub294 \ub2ec\ub9ac\xa0\uc774\ub7ec\ud55c \ubb38\ud654\uac00 \uc798 \uc720\uc9c0\ub418\uace0 \uc788\ub2e4\ub294 \uc810\uc774\ub2e4. \uc194\uc9c1\ud568\uc744 \ubc29\ud328\uc0bc\uc544 \ubd80\uc801\uc808\ud55c \uc758\uc0ac\uc18c\ud1b5\uc744 \ud558\ub294 \uc0ac\ub840\ub294 \uc544\uc9c1\uae4c\uc9c0 \uacbd\ud5d8\ud574\ubcf4\uc9c0 \ubabb\ud588\ub2e4.\\n\\n\uadf8\uc804\uc5d0\ub294 \uc2a4\uc2a4\ub85c \uad49\uc7a5\ud788 \uc870\uc2ec\uc2a4\ub7ec\uc6b4 \uc758\uc0ac\uc18c\ud1b5\uc744 \ud574\uc654\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uadf8\ub807\uae30\uc5d0 \uc785\uc0ac \ud6c4 \ucd5c\ub300\ud55c \uc194\uc9c1\ud558\uace0 \uc9c1\uad00\uc801\uc778 \uc758\uc0ac\uc18c\ud1b5\uc744 \ud558\uae30 \uc704\ud574 \ub178\ub825\ud588\ub2e4. \uc774\ubbf8 \ub9ce\uc740 \ub3d9\ub8cc\ub4e4\uc774 \uadf8\ub7f0 \ubaa8\uc2b5\uc744 \ubcf4\uc5ec\uc8fc\uace0 \uc788\uae30\uc5d0 \uc5b4\ub835\uc9c0 \uc54a\uac8c \uc801\uc751\ud560 \uc218 \uc788\uc5c8\ub358 \uac83 \uac19\uc544 \uac10\uc0ac\ud558\ub2e4.\\n\\n\uc194\uc9c1\ud568\uc758 \uc7a5\uc810\uc740 \uc624\ud574\uac00 \uc5c6\ub2e4\ub294 \uc810\uc774\ub2e4. \ub204\uad70\uac00\uc758 \ub9d0\uc774 \ub2e4\ub978 \uc758\ub3c4\uac00 \uc788\uc744\uc9c0 \uc758\uc2ec\ud558\uace0 \uace0\ubbfc\ud558\ub294 \uc77c\uc740 \uc0c1\ub2f9\ud788 \uc2a4\ud2b8\ub808\uc2a4\ubc1b\ub294 \uc77c\uc774\ub2e4. \uc9c1\uc7a5\uc5d0\uc11c \ub4e4\uc740 \ub9d0\uc758 \uc758\ubbf8\uac00 \ubb34\uc5c7\uc778\uc9c0\uc5d0 \ub300\ud55c \uace0\ubbfc\uc744 \ud1f4\uadfc \ud6c4 \uc9d1\uae4c\uc9c0 \uac00\uc838\uc624\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc744 \uac83\uc774\ub2e4. \uadf8\ub7ec\ub098 \uc11c\ub85c\uac00 \uc194\uc9c1\ud558\uac8c \ub9d0\ud55c\ub2e4\ub294 \ubbff\uc74c\uc774 \uc0dd\uae30\uba74 \uc788\ub294 \uadf8\ub300\ub85c \ubc1b\uc544\ub4e4\uc77c \uc218 \uc788\uc5b4 \uac04\ub2e8\ud558\uace0 \uba85\ub8cc\ud558\uac8c \ub300\ud654\ub97c \ub9c8\ubb34\ub9ac\uc9c0\uc744 \uc218 \uc788\ub2e4. \uc774\ub7f0 \uc810\uc774 \uc5c5\ubb34\uc5d0\uc11c \uc624\ub294 \uc5b4\ub824\uc6c0\uc744 \ud06c\uac8c \uc904\uc5ec\uc8fc\uace0, \uc11c\ub85c\uac04\uc758 \uc2e0\ub8b0\ub97c \uc313\ub294\ub370 \ub3c4\uc6c0\uc744 \uc8fc\ub294 \uac83 \uac19\ub2e4.\\n\\n---\\n\\n- \uae30\uc220\uc801 \ubc30\uc6c0\\n\\n\ucd08\uae30 \uc628\ubcf4\ub529\uacfc \uc774\ud6c4 \ub9e1\uc740 \uc5c5\ubb34\ub97c \uc9c4\ud589\ud558\uba70 \ud68c\uc0ac\uc5d0\uc11c \uc0ac\uc6a9\ud558\uace0 \uc788\ub294 \uae30\uc220\ub4e4\uc5d0 \uc775\uc219\ud574\uc9c8 \uc218 \uc788\uc5c8\ub2e4. redux\uc640 saga\ub97c \uc775\ud788\uace0 \uc788\ub294\ub370, \ud2b8\ub80c\ub514\ud558\uc9c0\ub294 \uc54a\ub354\ub77c\ub3c4 \ucda9\ubd84\ud788 \ubfb0\uc871\ud55c \uc7a5\uc810\uc774 \uc788\ub294 \uc870\ud569\uc774\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4. \ube60\ub974\uac8c \ud504\ub85c\uc81d\ud2b8 \ud558\ub098\ub97c \ub9e1\uc544\uc11c \ucd9c\uc2dc\ub97c \uc55e\ub450\uace0 \uc788\ub294\ub370, \ub9ce\uc740 \ub3c4\uc6c0\uc744 \uc8fc\ub294 \ub3d9\ub8cc\ub4e4\uacfc \ud568\uaed8 \ud560 \uc218 \uc788\uc5b4 \uc990\uac81\uac8c \uc77c\ud588\ub2e4\uace0 \uc0dd\uac01\uc774 \ub4e0\ub2e4."}]}')}}]);